import React, { useState } from "react";
import { useProduct } from "../context/ProductContextProvider";
import { Box, TextField, Button } from "@mui/material";

const AddCategory = () => {
  const { createSport } = useProduct();
  const [sport, setSport] = useState({
    name: "",
  });

  const handleChange = (e) => {
    setSport({ ...sport, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    createSport(sport);
    setSport({ ...sport, name: "" });
  };

  return (
    <Box
      component="form"
      onSubmit={handleSubmit}
      sx={{
        display: "flex",
        justifyContent: "center", // Центрируем по горизонтали
        alignItems: "center", // Центрируем по вертикали
        minHeight: "100vh", // Гарантируем, что контейнер занимает весь экран
        padding: 2,
        margin: "auto", // Центрируем по горизонтали
      }}
    >
      <Box
        sx={{
          width: "90%",
          maxWidth: "310px", // Ограничиваем максимальную ширину
          padding: 2,
          border: "1px solid black",
          borderRadius: 1,
          backgroundColor: "#b5ffe1",
          color: "#01263a",
          display: "flex",
          flexDirection: "column",
          justifyContent: "center",
          alignItems: "center",
        }}
      >
        <TextField
          type="text"
          name="name"
          value={sport.name}
          onChange={handleChange}
          label="Sport Name"
          variant="outlined"
          fullWidth
          sx={{ marginBottom: "16px", backgroundColor: "#ADE8FE" }}
        />
        <Button type="submit" variant="contained" color="primary">
          Add Category
        </Button>
      </Box>
    </Box>
  );
};

export default AddCategory;
