import React, { useState } from "react";
import { Button, Box, Typography } from "@mui/material";
import videoBg from "../homePage/assets/videoBg.mp4"; // Путь к видео

const MainPage = () => {
  const [isVisible, setIsVisible] = useState(false);
  const [questionIndex, setQuestionIndex] = useState(0);
  const [score, setScore] = useState(0);
  const [showResult, setShowResult] = useState(false);

  const questions = [
    {
      question: "Кто стал первым олимпийским чемпионом Кыргызстана?",
      options: [
        "Нурсултан Мухаммедов",
        "Айсулу Тыныбекова",
        "Азамат Кенжебаев",
      ],
      answer: "Нурсултан Мухаммедов",
    },
    {
      question: "В каком году Кыргызстан впервые выиграл олимпийскую медаль?",
      options: ["2000", "2004", "1996"],
      answer: "2000",
    },
    {
      question:
        "Какую медаль выиграл Нурсултан Мухаммедов на Олимпийских играх 2000 года?",
      options: ["Золото", "Серебро", "Бронза"],
      answer: "Бронза",
    },
    {
      question:
        "Какая спортсменка из Кыргызстана завоевала серебряную медаль на Олимпиаде 2020 года?",
      options: ["Айсулу Тыныбекова", "Наталья Батич", "Алтынбек Ануаров"],
      answer: "Айсулу Тыныбекова",
    },
  ];

  const handleAnswer = (selectedAnswer) => {
    if (selectedAnswer === questions[questionIndex].answer) {
      setScore(score + 1);
    }
    const nextIndex = questionIndex + 1;
    if (nextIndex < questions.length) {
      setQuestionIndex(nextIndex);
    } else {
      setShowResult(true);
    }
  };

  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 200) {
        setIsVisible(true);
      } else {
        setIsVisible(false);
      }
    };
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <Box
      className="home-main-page"
      sx={{
        position: "relative",
        minHeight: "100vh",
        overflow: "hidden",
        display: "flex",
        flexDirection: "column",
      }}
    >
      {/* Видео фон */}
      <Box
        sx={{
          position: "absolute",
          top: 0,
          left: 0,
          width: "100%",
          height: "100%",
          zIndex: -1,
        }}
      >
        <video
          className="background-video"
          src={videoBg}
          autoPlay
          loop
          muted
          playsInline
          style={{
            width: "100%",
            height: "100%",
            objectFit: "cover",
            filter: "brightness(70%)", // Сделал фон темнее для лучшего контраста
          }}
        />
        <div
          style={{
            position: "absolute",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            background:
              "linear-gradient(to bottom, rgba(0,0,0,0.5), rgba(0,0,0,0.9))", // Более темный оверлей
          }}
        ></div>
      </Box>

      {/* Контент с анимацией */}
      <Box
        sx={{
          position: "relative",
          width: "100%",
          minHeight: "100vh",
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          textAlign: "center",
          color: "white",
          padding: "20px",
          opacity: isVisible ? 1 : 0,
          transform: isVisible ? "translateY(0)" : "translateY(30px)", // Меньше движения
          transition: "all 0.8s ease-out",
        }}
      >
        <Box sx={{ maxWidth: "800px" }}>
          <Typography
            variant="h1"
            sx={{
              fontSize: "48px",
              marginBottom: "15px",
              fontWeight: "bold",
              fontFamily: "Georgia, serif",
              letterSpacing: "1px",
            }}
          >
            Спортсмены Кыргызстана
          </Typography>
          <Typography
            variant="body1"
            sx={{
              fontSize: "clamp(16px, 2vw, 22px)",
              fontWeight: "500",
              lineHeight: "1.6",
              maxWidth: "90%",
              margin: "0 auto",
            }}
          >
            Спортсмены Кыргызстана завоевывают признание на международной арене
            в таких видах спорта, как борьба, дзюдо, бокс и легкая атлетика. Эти
            атлеты становятся символами силы, упорства и решимости. Их
            достижения вдохновляют новое поколение, показывая, что стремление к
            победе и трудолюбие открывают путь к вершинам.
          </Typography>
        </Box>
      </Box>

      {/* Блок с изображением с эффектом параллакса */}
      <Box
        sx={{
          textAlign: "center",
          padding: "20px",
          backgroundColor: "#121212",
          display: "flex",
          justifyContent: "center",
          position: "relative",
        }}
      >
        <img
          src="https://data.kaktus.media/image/big/2024-08-11_19-52-39_202865.jpg"
          alt="Кыргызстан"
          style={{
            width: "90%",
            maxWidth: "1400px",
            borderRadius: "12px",
            boxShadow: "0 4px 15px rgba(255, 255, 255, 0.1)",
            transition: "transform 0.3s ease, filter 0.3s ease",
            transform: "scale(1)",
            filter: "brightness(80%)",
          }}
          onMouseOver={(e) => {
            e.target.style.transform = "scale(1.03)";
            e.target.style.filter = "brightness(100%)";
          }}
          onMouseOut={(e) => {
            e.target.style.transform = "scale(1)";
            e.target.style.filter = "brightness(80%)";
          }}
        />
      </Box>

      {/* Викторина */}
      <Box
        sx={{
          textAlign: "center",
          marginTop: "40px",
          padding: "20px",
          backgroundColor: "#1a1a1a",
          borderRadius: "10px",
        }}
      >
        {showResult ? (
          <Typography
            variant="h4"
            sx={{
              color: "#ffeb3b",
              fontWeight: "bold",
            }}
          >
            Вы набрали {score} из {questions.length} правильных ответов!
          </Typography>
        ) : (
          <>
            <Typography
              variant="h4"
              sx={{
                color: "#ffeb3b",
                fontWeight: "bold",
                marginBottom: "20px",
              }}
            >
              Викторина о олимпийских чемпионах Кыргызстана
            </Typography>
            <Typography
              variant="h5"
              sx={{
                color: "#fff",
                marginBottom: "10px",
              }}
            >
              {questions[questionIndex].question}
            </Typography>
            {questions[questionIndex].options.map((option, idx) => (
              <Button
                key={idx}
                sx={{
                  margin: "5px",
                  backgroundColor: "#2196f3",
                  "&:hover": {
                    backgroundColor: "#1976d2",
                  },
                }}
                onClick={() => handleAnswer(option)}
              >
                {option}
              </Button>
            ))}
          </>
        )}
      </Box>
    </Box>
  );
};

export default MainPage;

