import React, { useEffect, useState } from "react";
import { Button, Box, Typography, Card, CardContent } from "@mui/material";
import videoBg from "../homePage/assets/videoBg.mp4";

const MainPage = () => {
  const [isVisible, setIsVisible] = useState(false);
  const [selectedAnswer, setSelectedAnswer] = useState(null);
  const [isCorrect, setIsCorrect] = useState(null);

  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 200) {
        setIsVisible(true);
      } else {
        setIsVisible(false);
      }
    };
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  const question = "–ö–∞–∫–æ–π —Å–∞–º—ã–π –≤—ã—Å–æ–∫–∏–π –ø–∏–∫ –≤ –ö—ã—Ä–≥—ã–∑—Å—Ç–∞–Ω–µ?";
  const answers = ["–ü–∏–∫ –õ–µ–Ω–∏–Ω–∞", "–ü–∏–∫ –ü–æ–±–µ–¥—ã", "–ü–∏–∫ –ú–∞–Ω–∞—Å", "–ü–∏–∫ –ö–æ—Ä–æ–Ω–∞"];
  const correctAnswer = "–ü–∏–∫ –ü–æ–±–µ–¥—ã";

  const handleAnswerClick = (answer) => {
    setSelectedAnswer(answer);
    setIsCorrect(answer === correctAnswer);
  };

  return (
    <Box
      className="home-main-page"
      sx={{ position: "relative", minHeight: "100vh", overflow: "hidden" }}
    >
      {/* –í–∏–¥–µ–æ —Ñ–æ–Ω */}
      <Box
        sx={{
          position: "absolute",
          top: 0,
          left: 0,
          width: "100%",
          height: "100%",
          zIndex: -1,
        }}
      >
        <video
          src={videoBg}
          autoPlay
          loop
          muted
          playsInline
          style={{
            width: "100%",
            height: "100%",
            objectFit: "cover",
            filter: "brightness(85%)",
          }}
        />
      </Box>

      {/* –ö–æ–Ω—Ç–µ–Ω—Ç */}
      <Box
        sx={{
          position: "relative",
          minHeight: "100vh",
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          justifyContent: "center",
          textAlign: "center",
          color: "white",
          padding: "20px",
          opacity: isVisible ? 1 : 0,
          transform: isVisible ? "translateY(0)" : "translateY(30px)",
          transition: "all 0.8s ease-out",
        }}
      >
        <Typography variant="h1" sx={{ fontSize: "48px", fontWeight: "bold" }}>
          –°–ø–æ—Ä—Ç—Å–º–µ–Ω—ã –ö—ã—Ä–≥—ã–∑—Å—Ç–∞–Ω–∞
        </Typography>
      </Box>

      {/* –§–æ—Ç–æ */}
      <Box
        sx={{
          textAlign: "center",
          padding: "20px",
          backgroundColor: "#121212",
        }}
      >
        <img
          src="https://data.kaktus.media/image/big/2024-08-11_19-52-39_202865.jpg"
          alt="–ö—ã—Ä–≥—ã–∑—Å—Ç–∞–Ω"
          style={{
            width: "90%",
            maxWidth: "1400px",
            borderRadius: "12px",
            boxShadow: "0 4px 15px rgba(255, 255, 255, 0.1)",
            transition: "transform 0.3s ease, filter 0.3s ease",
          }}
        />
      </Box>

      {/* –í–∏–∫—Ç–æ—Ä–∏–Ω–∞ */}
      <Box
        sx={{
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          textAlign: "center",
          backgroundColor: "#1e1e1e",
          padding: "40px",
          borderRadius: "12px",
          boxShadow: "0 4px 15px rgba(255, 255, 255, 0.1)",
          margin: "30px auto",
          maxWidth: "600px",
        }}
      >
        <Typography variant="h4" sx={{ color: "white", marginBottom: "20px" }}>
          {question}
        </Typography>
        {answers.map((answer, index) => (
          <Card
            key={index}
            onClick={() => handleAnswerClick(answer)}
            sx={{
              cursor: "pointer",
              margin: "10px",
              width: "100%",
              transition: "all 0.3s ease",
              backgroundColor:
                selectedAnswer === answer
                  ? isCorrect
                    ? "green"
                    : "red"
                  : "#2e2e2e",
              color: "white",
              "&:hover": {
                transform: "scale(1.05)",
                backgroundColor: "#444",
              },
            }}
          >
            <CardContent>
              <Typography>{answer}</Typography>
            </CardContent>
          </Card>
        ))}
        {selectedAnswer && (
          <Typography
            sx={{ marginTop: "20px", color: isCorrect ? "lightgreen" : "red" }}
          >
            {isCorrect ? "–í–µ—Ä–Ω–æ! üèîÔ∏è" : "–ù–µ–≤–µ—Ä–Ω–æ! –ü–æ–ø—Ä–æ–±—É–π—Ç–µ –µ—â—ë —Ä–∞–∑."}
          </Typography>
        )}
      </Box>
    </Box>
  );
};

export default MainPage;

