import React from "react";
import { Card, CardContent, Typography, Grid } from "@mui/material";
import { styled } from "@mui/system";

// Стилизация с помощью styled API
const StyledCard = styled(Card)({
  marginBottom: "20px",
});

const Title = styled(Typography)({
  fontWeight: "bold",
  fontSize: "1.2rem",
});

const DateText = styled(Typography)({
  color: "#888",
  fontSize: "0.9rem",
});

const Description = styled(Typography)({
  fontSize: "1rem",
  color: "#555",
});

const newsData = [
  {
    id: 1,
    title: "Серебро на чемпионате мира по дзюдо!",
    date: "25 октября 2024",
    description: "В США впервые прошел турнир по Фиджитал-хоккею!",
  },
  {
    id: 2,
    title: "Золото на международных соревнованиях по легкой атлетике",
    date: "18 ноября 2024",
    description:
      "На международных соревнованиях по легкой атлетике спортсмен из Кыргызстана занял первое место, установив новый национальный рекорд.",
  },
  {
    id: 3,
    title: "Кыргызстанские футболисты готовятся к Кубку Азии 2025",
    date: "12 декабря 2024",
    description:
      "Футболисты Кыргызстана intensively готовятся к Кубку Азии 2025, тренировки проходят в лучших спортивных лагерях.",
  },
  {
    id: 4,
    title: "Зимние игры 2025: спортсмены Кыргызстана на пути к победе",
    date: "3 января 2025",
    description:
      "Сборная Кыргызстана готовится к Зимним Олимпийским играм 2025, ожидаются серьезные выступления в горнолыжном спорте и биатлоне.",
  },
];

const NewsPage = () => {
  return (
    <div>
      <Typography variant="h4" align="center" gutterBottom>
        Новости о спортсменах Кыргызстана (2024-2025)
      </Typography>
      <Grid container spacing={3}>
        {newsData.map((news) => (
          <Grid item xs={12} md={6} key={news.id}>
            <StyledCard>
              <CardContent>
                <Title>{news.title}</Title>
                <DateText>{news.date}</DateText>
                <Description>{news.description}</Description>
              </CardContent>
            </StyledCard>
          </Grid>
        ))}
      </Grid>
    </div>
  );
};

export default NewsPage;
