import React, { useState } from "react";
import {
  Button,
  Box,
  Typography,
  Card,
  CardContent,
  Chip,
  Grid,
} from "@mui/material";
import { green, red, blue } from "@mui/material/colors";

const Quiz = () => {
  const questions = [
    {
      question: "Какой вид спорта популярен в Кыргызстане?",
      options: ["Бокс", "Футбол", "Теннис", "Плавание"],
      answer: "Бокс",
    },
    {
      question: "Какая столица Кыргызстана?",
      options: ["Бишкек", "Алматы", "Ташкент", "Астана"],
      answer: "Бишкек",
    },
    {
      question: "Когда был основан современный Олимпийский комитет?",
      options: ["1896", "1900", "1912", "1945"],
      answer: "1896",
    },
  ];

  const [selectedAnswers, setSelectedAnswers] = useState([]);
  const [showResults, setShowResults] = useState(false);

  const handleAnswer = (questionIndex, selectedAnswer) => {
    const newAnswers = [...selectedAnswers];
    newAnswers[questionIndex] = selectedAnswer;
    setSelectedAnswers(newAnswers);
  };

  const handleSubmit = () => {
    setShowResults(true);
  };

  return (
    <Box
      sx={{ padding: "30px", backgroundColor: "#f4f4f9", borderRadius: "10px" }}
    >
      <Typography
        variant="h4"
        sx={{ textAlign: "center", marginBottom: "20px", color: "#333" }}
      >
        Викторина о Кыргызстане
      </Typography>

      <Grid
        container
        spacing={2}
        direction="column"
        sx={{ alignItems: "center" }}
      >
        {questions.map((q, index) => (
          <Grid item key={index} xs={12} md={6}>
            <Card
              sx={{
                width: "100%",
                marginBottom: "20px",
                boxShadow: 2,
                borderRadius: "15px",
                backgroundColor: "#fff",
                transition: "all 0.3s ease",
                "&:hover": {
                  transform: "scale(1.05)",
                  boxShadow: "0 10px 20px rgba(0,0,0,0.2)",
                },
              }}
            >
              <CardContent>
                <Typography
                  variant="h6"
                  sx={{ fontWeight: "bold", marginBottom: "15px" }}
                >
                  {q.question}
                </Typography>
                <Grid container spacing={1}>
                  {q.options.map((option, idx) => (
                    <Grid item key={idx} xs={12} sm={6}>
                      <Button
                        fullWidth
                        variant="outlined"
                        onClick={() => handleAnswer(index, option)}
                        sx={{
                          backgroundColor:
                            selectedAnswers[index] === option
                              ? selectedAnswers[index] === q.answer
                                ? green[500]
                                : red[500]
                              : blue[500],
                          color: "white",
                          fontWeight: "bold",
                          "&:hover": {
                            backgroundColor:
                              selectedAnswers[index] === option
                                ? selectedAnswers[index] === q.answer
                                  ? green[700]
                                  : red[700]
                                : blue[700],
                          },
                        }}
                      >
                        {option}
                      </Button>
                    </Grid>
                  ))}
                </Grid>
                {showResults && (
                  <Box sx={{ marginTop: "15px" }}>
                    {selectedAnswers[index] && (
                      <Chip
                        label={
                          selectedAnswers[index] === q.answer
                            ? "Правильно"
                            : "Неправильно"
                        }
                        color={
                          selectedAnswers[index] === q.answer
                            ? "success"
                            : "error"
                        }
                        sx={{ fontWeight: "bold" }}
                      />
                    )}
                  </Box>
                )}
              </CardContent>
            </Card>
          </Grid>
        ))}

        <Grid item xs={12}>
          <Button
            variant="contained"
            color="primary"
            sx={{
              fontSize: "16px",
              fontWeight: "bold",
              padding: "10px 30px",
              borderRadius: "30px",
              transition: "all 0.3s ease",
              "&:hover": {
                backgroundColor: "#1976d2",
                boxShadow: "0 4px 10px rgba(0,0,0,0.2)",
              },
            }}
            onClick={handleSubmit}
          >
            Отправить ответы
          </Button>
        </Grid>
      </Grid>
    </Box>
  );
};

const MainPage = () => {
  return (
    <Box
      sx={{
        position: "relative",
        minHeight: "100vh",
        overflow: "hidden",
        display: "flex",
        flexDirection: "column",
      }}
    >
      {/* Другие элементы страницы */}
      <Box sx={{ padding: "40px", backgroundColor: "#121212" }}>
        <Quiz />
      </Box>
    </Box>
  );
};

export default MainPage;

