import React, { useState } from "react";
import { Button, Box, Typography } from "@mui/material";

const MainPage = () => {
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [selectedOption, setSelectedOption] = useState(null);
  const [quizFeedback, setQuizFeedback] = useState("");
  const [isAnswerCorrect, setIsAnswerCorrect] = useState(null);

  const questions = [
    {
      question:
        "–ö—Ç–æ –∏–∑ —Å–ø–æ—Ä—Ç—Å–º–µ–Ω–æ–≤ –ö—ã—Ä–≥—ã–∑—Å—Ç–∞–Ω–∞ —Å—Ç–∞–ª –æ–ª–∏–º–ø–∏–π—Å–∫–∏–º —á–µ–º–ø–∏–æ–Ω–æ–º –≤ –¢–æ–∫–∏–æ?",
      options: [
        "–ì—É–ª—å–Ω–∞—Ä–∞ –ë–µ–∫–º–∞–º–∞—Ç–æ–≤–∞",
        "–ú–µ–¥–µ—Ç –°–∞–π–¥—É–ª–∞–µ–≤",
        "–£–ª–∞–Ω–±–µ–∫ –ê–º–∏—Ä–æ–≤",
        "–°–∞–∏–¥–∂–æ–Ω –ú–∞–¥–∞–ª–∏–µ–≤",
      ],
      correct: 0,
    },
    {
      question:
        "–ö–∞–∫–æ–π –≤–∏–¥ —Å–ø–æ—Ä—Ç–∞ –ø—Ä–∏–Ω–µ—Å –ö—ã—Ä–≥—ã–∑—Å—Ç–∞–Ω—É –ø–µ—Ä–≤—É—é –æ–ª–∏–º–ø–∏–π—Å–∫—É—é –º–µ–¥–∞–ª—å?",
      options: ["–ë–æ–∫—Å", "–ë–æ—Ä—å–±–∞", "–î–∑—é–¥–æ", "–õ–µ–≥–∫–∞—è –∞—Ç–ª–µ—Ç–∏–∫–∞"],
      correct: 1,
    },
    {
      question:
        "–°–∫–æ–ª—å–∫–æ –º–µ–¥–∞–ª–µ–π –∑–∞–≤–æ–µ–≤–∞–ª–∏ —Å–ø–æ—Ä—Ç—Å–º–µ–Ω—ã –ö—ã—Ä–≥—ã–∑—Å—Ç–∞–Ω–∞ –Ω–∞ –û–ª–∏–º–ø–∏–∞–¥–µ 2016 –≥–æ–¥–∞?",
      options: ["1 –º–µ–¥–∞–ª—å", "2 –º–µ–¥–∞–ª–∏", "3 –º–µ–¥–∞–ª–∏", "4 –º–µ–¥–∞–ª–∏"],
      correct: 1,
    },
  ];

  const handleOptionClick = (index) => {
    setSelectedOption(index);
    const correctOption = questions[currentQuestion].correct;
    if (index === correctOption) {
      setIsAnswerCorrect(true);
      setQuizFeedback("üéâ –ü—Ä–∞–≤–∏–ª—å–Ω—ã–π –æ—Ç–≤–µ—Ç! –û—Ç–ª–∏—á–Ω–æ!");
    } else {
      setIsAnswerCorrect(false);
      setQuizFeedback("‚ùå –ù–µ–ø—Ä–∞–≤–∏–ª—å–Ω—ã–π –æ—Ç–≤–µ—Ç, –ø–æ–ø—Ä–æ–±—É–π—Ç–µ —Å–Ω–æ–≤–∞.");
    }
  };

  const handleNextQuestion = () => {
    setSelectedOption(null);
    setQuizFeedback("");
    setIsAnswerCorrect(null);
    setCurrentQuestion(currentQuestion + 1);
  };

  const currentQ = questions[currentQuestion];

  return (
    <Box
      sx={{
        padding: "40px 20px",
        backgroundColor: "#2c2c2c",
        color: "white",
        textAlign: "center",
        marginTop: "30px",
        borderRadius: "10px",
        boxShadow: "0 4px 15px rgba(255, 255, 255, 0.1)",
      }}
    >
      <Typography
        variant="h2"
        sx={{
          marginBottom: "20px",
          fontWeight: "bold",
          color: "#ffcc00",
          fontSize: "28px",
          letterSpacing: "2px",
          textTransform: "uppercase",
        }}
      >
        –í–∏–∫—Ç–æ—Ä–∏–Ω–∞: –û–ª–∏–º–ø–∏–π—Å–∫–∏–µ —á–µ–º–ø–∏–æ–Ω—ã –ö—ã—Ä–≥—ã–∑—Å—Ç–∞–Ω–∞
      </Typography>

      <Box sx={{ marginBottom: "30px" }}>
        <Typography
          variant="h5"
          sx={{
            marginBottom: "15px",
            fontSize: "22px",
            fontWeight: "600",
            textAlign: "center",
            color: "#fff",
          }}
        >
          {currentQ.question}
        </Typography>
        <Box
          sx={{
            display: "flex",
            flexDirection: "column",
            gap: "15px",
            alignItems: "center",
          }}
        >
          {currentQ.options.map((option, idx) => (
            <Button
              key={idx}
              variant="contained"
              color={selectedOption === idx ? "success" : "primary"}
              onClick={() => handleOptionClick(idx)}
              sx={{
                background:
                  selectedOption === idx
                    ? "linear-gradient(135deg, #ff4d6d, #ff9100)"
                    : "#007bff",
                transition: "all 0.3s ease",
                ":hover": {
                  transform: "scale(1.1)",
                  background: "#ff4d6d",
                },
                padding: "12px 25px",
                borderRadius: "10px",
                fontSize: "16px",
                boxShadow: "0 4px 10px rgba(0,0,0,0.3)",
              }}
            >
              {option}
            </Button>
          ))}
        </Box>

        {quizFeedback && (
          <Typography
            sx={{
              marginTop: "20px",
              fontWeight: "bold",
              color: isAnswerCorrect ? "green" : "red",
              fontSize: "20px",
              animation: "fadeIn 0.5s ease-out",
            }}
          >
            {quizFeedback}
          </Typography>
        )}

        {currentQuestion < questions.length - 1 ? (
          <Button
            variant="contained"
            color="secondary"
            onClick={handleNextQuestion}
            sx={{
              marginTop: "30px",
              padding: "12px 20px",
              fontSize: "18px",
              borderRadius: "10px",
              boxShadow: "0 4px 10px rgba(0,0,0,0.3)",
              transition: "transform 0.3s ease",
              ":hover": { transform: "scale(1.05)" },
            }}
          >
            –°–ª–µ–¥—É—é—â–∏–π –≤–æ–ø—Ä–æ—Å
          </Button>
        ) : (
          <Typography
            sx={{
              marginTop: "20px",
              fontSize: "22px",
              fontWeight: "bold",
              color: "#ffcc00",
            }}
          >
            –í–∏–∫—Ç–æ—Ä–∏–Ω–∞ –∑–∞–≤–µ—Ä—à–µ–Ω–∞! –°–ø–∞—Å–∏–±–æ –∑–∞ —É—á–∞—Å—Ç–∏–µ!
          </Typography>
        )}
      </Box>
    </Box>
  );
};

export default MainPage;

