import React, { useState } from "react";

const Auth = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [emailError, setEmailError] = useState(""); // определяем здесь
  const [passwordError, setPasswordError] = useState(""); // определяем здесь

  const handleSubmit = (event) => {
    event.preventDefault();

    // Простая проверка на ошибки
    if (!email) {
      setEmailError("Email is required");
    } else {
      setEmailError("");
    }

    if (!password) {
      setPasswordError("Password is required");
    } else {
      setPasswordError("");
    }

    // Добавьте вашу логику отправки формы
    if (email && password) {
      console.log("Form Submitted");
      // Отправьте запрос на сервер или выполните другую логику
    }
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Email:</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          {emailError && <span>{emailError}</span>}
        </div>
        <div>
          <label>Password:</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          {passwordError && <span>{passwordError}</span>}
        </div>
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default Auth;
