import React from "react";
import { useAuth } from "../context/AuthContextProvider";

const AdminPage = () => {
  const { user } = useAuth();

  // Проверка роли пользователя (предположим, что у вас есть информация о ролях в user)
  const isAdmin = user && user.email === "admin@example.com"; // Пример проверки для администратора

  if (!isAdmin) {
    return <div>У вас нет прав для доступа к этой странице.</div>;
  }

  return (
    <div>
      <h2>Добро пожаловать на страницу администратора</h2>
      {/* Контент для админа */}
    </div>
  );
};

export default AdminPage;
