import React, { useState } from "react";
import {
  Box,
  Button,
  Typography,
  Radio,
  RadioGroup,
  FormControl,
  FormControlLabel,
  FormLabel,
  Paper,
} from "@mui/material";
import { CheckCircle, Cancel } from "@mui/icons-material";

const Quiz = () => {
  const [selectedAnswer, setSelectedAnswer] = useState("");
  const [isAnswered, setIsAnswered] = useState(false);
  const [correctAnswer] = useState("B"); // Правильный ответ

  const handleAnswerChange = (event) => {
    setSelectedAnswer(event.target.value);
  };

  const handleSubmit = () => {
    setIsAnswered(true);
  };

  return (
    <Box sx={{ display: "flex", justifyContent: "center", marginTop: "50px" }}>
      <Paper
        sx={{
          padding: "40px",
          borderRadius: "12px",
          boxShadow: "0 10px 30px rgba(0, 0, 0, 0.1)",
          width: "100%",
          maxWidth: "600px",
          background: "#1e1e1e",
        }}
      >
        <Typography
          variant="h4"
          sx={{
            color: "#fff",
            fontWeight: "bold",
            marginBottom: "30px",
            fontFamily: "Georgia, serif",
          }}
        >
          Викторина: Кто выиграл золотую медаль на Олимпийских играх 2024 года?
        </Typography>
        <FormControl component="fieldset">
          <FormLabel
            component="legend"
            sx={{ color: "#fff", fontSize: "18px" }}
          >
            Выберите правильный ответ:
          </FormLabel>
          <RadioGroup value={selectedAnswer} onChange={handleAnswerChange}>
            <FormControlLabel
              value="A"
              control={
                <Radio
                  sx={{
                    color: "#2196f3",
                    "&.Mui-checked": { color: "#2196f3" },
                  }}
                />
              }
              label={
                <Typography sx={{ color: "#fff" }}>A. Джон Смит</Typography>
              }
            />
            <FormControlLabel
              value="B"
              control={
                <Radio
                  sx={{
                    color: "#2196f3",
                    "&.Mui-checked": { color: "#2196f3" },
                  }}
                />
              }
              label={
                <Typography sx={{ color: "#fff" }}>B. Майкл Джордан</Typography>
              }
            />
            <FormControlLabel
              value="C"
              control={
                <Radio
                  sx={{
                    color: "#2196f3",
                    "&.Mui-checked": { color: "#2196f3" },
                  }}
                />
              }
              label={
                <Typography sx={{ color: "#fff" }}>C. Усейн Болт</Typography>
              }
            />
            <FormControlLabel
              value="D"
              control={
                <Radio
                  sx={{
                    color: "#2196f3",
                    "&.Mui-checked": { color: "#2196f3" },
                  }}
                />
              }
              label={
                <Typography sx={{ color: "#fff" }}>D. Кеша Таллин</Typography>
              }
            />
          </RadioGroup>
          <Box
            sx={{
              display: "flex",
              justifyContent: "center",
              marginTop: "30px",
            }}
          >
            <Button
              variant="contained"
              color="primary"
              onClick={handleSubmit}
              sx={{
                backgroundColor: "#ff4d6d",
                "&:hover": { backgroundColor: "#e03e56" },
                padding: "10px 20px",
                fontSize: "16px",
              }}
            >
              Проверить ответ
            </Button>
          </Box>
        </FormControl>

        {isAnswered && (
          <Box sx={{ marginTop: "20px", textAlign: "center" }}>
            {selectedAnswer === correctAnswer ? (
              <Box sx={{ color: "green", fontSize: "24px" }}>
                <CheckCircle sx={{ fontSize: "40px", color: "green" }} />
                <Typography
                  variant="h6"
                  sx={{ color: "#fff", marginTop: "10px" }}
                >
                  Ответ правильный!
                </Typography>
              </Box>
            ) : (
              <Box sx={{ color: "red", fontSize: "24px" }}>
                <Cancel sx={{ fontSize: "40px", color: "red" }} />
                <Typography
                  variant="h6"
                  sx={{ color: "#fff", marginTop: "10px" }}
                >
                  Ответ неправильный. Попробуйте снова!
                </Typography>
              </Box>
            )}
          </Box>
        )}
      </Paper>
    </Box>
  );
};

export default Quiz;

