import React, { createContext, useContext, useEffect, useState } from "react";
import fire from "../../fire";
import { useNavigate } from "react-router-dom";

const authContext = createContext();
export const useAuth = () => useContext(authContext);

const AuthContextProvider = ({ children }) => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [user, setUser] = useState(null); // Стартовое значение null
  const [emailError, setEmailError] = useState("");
  const [passwordError, setPasswordError] = useState("");
  const [hasAccount, setHasAccount] = useState(false);
  const navigate = useNavigate();

  const clearInputs = () => {
    setEmail("");
    setPassword("");
  };

  const clearErrors = () => {
    setEmailError("");
    setPasswordError("");
  };

  //! REGISTER
  const handleRegister = () => {
    fire
      .auth()
      .createUserWithEmailAndPassword(email, password)
      .then(() => setHasAccount(true))
      .catch((error) => {
        switch (error.code) {
          case "auth/email-already-in-use":
          case "auth/invalid-email":
            setEmailError(error.message);
            break;
          case "auth/weak-password":
            setPasswordError(error.message);
            break;
          default:
            break;
        }
      });
    clearInputs();
    clearErrors();
  };

  //! LOGIN
  const handleLogin = () => {
    fire
      .auth()
      .signInWithEmailAndPassword(email, password)
      .then((userCredential) => {
        const user = userCredential.user;

        // Проверяем, является ли пользователь администратором
        if (user.email === "admin@example.com") {
          navigate("/admin"); // Редирект на админскую страницу
        } else {
          navigate("/"); // Редирект на главную страницу
        }
      })
      .catch((error) => {
        switch (error.code) {
          case "auth/user-disabled":
          case "auth/invalid-email":
          case "auth/user-not-found":
            setEmailError("Пользователь не найден или отключен");
            break;
          case "auth/wrong-password":
            setPasswordError("Неверный пароль");
            break;
          default:
            break;
        }
      });
    clearInputs();
    clearErrors();
  };

  //! LOGOUT
  const handleLogOut = () => {
    fire.auth().signOut();
    setUser(null); // Сброс состояния пользователя при выходе
    navigate("/"); // Редирект на главную страницу после выхода
  };

  const authListener = () => {
    fire.auth().onAuthStateChanged((user) => {
      if (user) {
        setUser(user);
        // Автоматический редирект в зависимости от роли пользователя
        if (user.email === "admin@example.com") {
          navigate("/admin");
        } else {
          navigate("/");
        }
      } else {
        setUser(null);
      }
    });
  };

  useEffect(() => {
    authListener();
  }, []);

  const values = {
    email,
    password,
    emailError,
    passwordError,
    setEmail,
    setPassword,
    setEmailError,
    setPasswordError,
    user,
    setUser,
    handleRegister,
    handleLogin,
    handleLogOut,
    authListener,
    hasAccount,
    setHasAccount,
  };

  return <authContext.Provider value={values}>{children}</authContext.Provider>;
};

export default AuthContextProvider;
