import React, { useEffect, useState } from "react";
import {
  AppBar,
  Toolbar,
  Typography,
  IconButton,
  Drawer,
  List,
  ListItem,
  ListItemText,
} from "@mui/material";
import { Link } from "react-router-dom";
import MenuIcon from "@mui/icons-material/Menu";
import CloseIcon from "@mui/icons-material/Close";
import { debounce } from "lodash"; // Импортируем debounce для оптимизации

const NavbarContent = ({ isAdmin, isMobile }) => {
  const [drawerOpen, setDrawerOpen] = useState(false);
  const [pages, setPages] = useState([
    { id: 1, title: "Атлеты", link: "/athletes" },
    { id: 2, title: "Виды спорта", link: "/sports" },
    { id: 3, title: "Медальный зачет", link: "/medal-table" },
  ]);

  useEffect(() => {
    if (isAdmin) {
      setPages((prevPages) => [
        ...prevPages,
        { id: 4, title: "Админ", link: "/admin" },
      ]);
    }
  }, [isAdmin]);

  const toggleDrawer = (open) => {
    setDrawerOpen(open);
  };

  return (
    <AppBar position="static" style={styles.appBar}>
      <Toolbar style={styles.toolbar}>
        <div style={styles.leftItems}>
          <Link to={"/"} style={{ textDecoration: "none" }}>
            <Typography variant="h6" style={styles.menuItem}>
              Спортсмены КР
            </Typography>
          </Link>
        </div>

        {!isMobile && (
          <div style={{ ...styles.centerItems, flex: 1 }}>
            {pages.map((page) => (
              <IconButton
                key={page.id}
                color="inherit"
                component={Link}
                to={page.link}
                style={styles.menuButton}
              >
                <Typography variant="body1" style={styles.menuItem}>
                  {page.title}
                </Typography>
              </IconButton>
            ))}
          </div>
        )}

        <div style={styles.rightItems}>
          <IconButton
            edge="end"
            color="inherit"
            aria-label="menu"
            onClick={() => toggleDrawer(true)}
            style={styles.menuButton}
          >
            <MenuIcon />
          </IconButton>
        </div>
      </Toolbar>

      <Drawer
        anchor="right"
        open={drawerOpen}
        onClose={() => toggleDrawer(false)}
        style={styles.drawer}
      >
        <div style={styles.drawerHeader}>
          <IconButton
            onClick={() => toggleDrawer(false)}
            style={styles.closeButton}
          >
            <CloseIcon />
          </IconButton>
        </div>
        <List style={styles.drawerList}>
          {pages.map((page) => (
            <ListItem button key={page.id} component={Link} to={page.link}>
              <ListItemText primary={page.title} />
            </ListItem>
          ))}
        </List>
      </Drawer>
    </AppBar>
  );
};

const styles = {
  appBar: {
    backgroundColor: "#1a1a1a",
    color: "#ffffff",
  },
  toolbar: {
    display: "flex",
    justifyContent: "space-between",
  },
  leftItems: {
    display: "flex",
    alignItems: "center",
  },
  centerItems: {
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
  },
  rightItems: {
    display: "flex",
    alignItems: "center",
  },
  menuButton: {
    marginRight: 10,
  },
  menuItem: {
    color: "#ffffff",
  },
  drawerList: {
    width: 250,
  },
  drawer: {
    backgroundColor: "rgba(0, 0, 0, 0.9)",
  },
  drawerHeader: {
    display: "flex",
    justifyContent: "flex-end",
    padding: "10px",
  },
  closeButton: {
    color: "#000",
  },
};

export default NavbarContent;
