import React, { useEffect, useState } from "react";
import { Button, Box, Typography, CircularProgress } from "@mui/material";
import videoBg from "../homePage/assets/videoBg.mp4"; // Путь к видео

const MainPage = () => {
  const [isVisible, setIsVisible] = useState(false);
  const [showQuiz, setShowQuiz] = useState(false);
  const [selectedOption, setSelectedOption] = useState(null);
  const [quizFeedback, setQuizFeedback] = useState("");
  const [quizResults, setQuizResults] = useState(0);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [isQuizFinished, setIsQuizFinished] = useState(false);

  const questions = [
    {
      question:
        "Кто из спортсменов Кыргызстана стал олимпийским чемпионом в Токио?",
      options: [
        "Гульнара Бекмаматова",
        "Медет Сайдулаев",
        "Уланбек Амиров",
        "Саиджон Мадалиев",
      ],
      correct: 0,
    },
    {
      question:
        "Какой вид спорта принес Кыргызстану первую олимпийскую медаль?",
      options: ["Бокс", "Борьба", "Дзюдо", "Легкая атлетика"],
      correct: 1,
    },
    {
      question:
        "Сколько медалей завоевали спортсмены Кыргызстана на Олимпиаде 2016 года?",
      options: ["1 медаль", "2 медали", "3 медали", "4 медали"],
      correct: 1,
    },
  ];

  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 200) {
        setIsVisible(true);
      } else {
        setIsVisible(false);
      }
    };
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  const handleOptionClick = (index) => {
    setSelectedOption(index);
    const correctOption = questions[currentQuestionIndex].correct;
    if (index === correctOption) {
      setQuizFeedback("Правильный ответ!");
      setQuizResults(quizResults + 1);
    } else {
      setQuizFeedback("Неправильный ответ, попробуйте снова.");
    }
  };

  const handleNextQuestion = () => {
    setSelectedOption(null);
    setQuizFeedback("");
    if (currentQuestionIndex < questions.length - 1) {
      setCurrentQuestionIndex(currentQuestionIndex + 1);
    } else {
      setIsQuizFinished(true);
    }
  };

  return (
    <Box
      className="home-main-page"
      sx={{
        position: "relative",
        minHeight: "100vh",
        overflow: "hidden",
        display: "flex",
        flexDirection: "column",
      }}
    >
      {/* Видео фон */}
      <Box
        sx={{
          position: "absolute",
          top: 0,
          left: 0,
          width: "100%",
          height: "100%",
          zIndex: -1,
        }}
      >
        <video
          className="background-video"
          src={videoBg}
          autoPlay
          loop
          muted
          playsInline
          style={{
            width: "100%",
            height: "100%",
            objectFit: "cover",
            filter: "brightness(80%)", // Сделал фон темнее для лучшего контраста
          }}
        />
        <div
          style={{
            position: "absolute",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            background:
              "linear-gradient(to bottom, rgba(0,0,0,0.5), rgba(0,0,0,0.9))", // Более темный оверлей
          }}
        ></div>
      </Box>

      {/* Контент с анимацией */}
      <Box
        sx={{
          position: "relative",
          width: "100%",
          minHeight: "100vh",
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          textAlign: "center",
          color: "white",
          padding: "20px",
          opacity: isVisible ? 1 : 0,
          transform: isVisible ? "translateY(0)" : "translateY(30px)", // Меньше движения
          transition: "all 0.8s ease-out",
        }}
      >
        <Box sx={{ maxWidth: "800px" }}>
          <Typography
            variant="h1"
            sx={{
              fontSize: "48px",
              marginBottom: "15px",
              fontWeight: "bold",
              fontFamily: "Georgia, serif",
              letterSpacing: "1px",
            }}
          >
            Спортсмены Кыргызстана
          </Typography>
          <Typography
            variant="body1"
            sx={{
              fontSize: "clamp(16px, 2vw, 22px)",
              fontWeight: "500",
              lineHeight: "1.6",
              maxWidth: "90%",
              margin: "0 auto",
            }}
          >
            Спортсмены Кыргызстана завоевывают признание на международной арене
            в таких видах спорта, как борьба, дзюдо, бокс и легкая атлетика. Эти
            атлеты становятся символами силы, упорства и решимости. Их
            достижения вдохновляют новое поколение, показывая, что стремление к
            победе и трудолюбие открывают путь к вершинам.
          </Typography>
        </Box>
      </Box>

      {/* Викторина */}
      <Box
        sx={{
          padding: "40px 20px",
          backgroundColor: "#2c2c2c",
          color: "white",
          textAlign: "center",
          marginTop: "30px",
          borderRadius: "10px",
        }}
      >
        <Typography
          variant="h2"
          sx={{ marginBottom: "20px", fontWeight: "bold" }}
        >
          Викторина: Олимпийские чемпионы Кыргызстана
        </Typography>
        {!isQuizFinished ? (
          <>
            <Box sx={{ marginBottom: "20px" }}>
              <Typography variant="h5" sx={{ marginBottom: "10px" }}>
                {questions[currentQuestionIndex].question}
              </Typography>
              <Box
                sx={{ display: "flex", justifyContent: "center", gap: "15px" }}
              >
                {questions[currentQuestionIndex].options.map((option, idx) => (
                  <Button
                    key={idx}
                    variant="contained"
                    color={selectedOption === idx ? "secondary" : "primary"}
                    onClick={() => handleOptionClick(idx)}
                    sx={{
                      transition: "all 0.3s ease",
                      ":hover": { transform: "scale(1.05)" },
                      padding: "10px 20px",
                    }}
                  >
                    {option}
                  </Button>
                ))}
              </Box>
              <Typography sx={{ marginTop: "10px", color: "yellow" }}>
                {quizFeedback}
              </Typography>
            </Box>
            <Button
              variant="contained"
              color="secondary"
              onClick={handleNextQuestion}
              sx={{
                marginTop: "20px",
                ":hover": { transform: "scale(1.05)" },
                padding: "12px 20px",
              }}
            >
              Далее
            </Button>
          </>
        ) : (
          <Box>
            <Typography variant="h5" sx={{ marginBottom: "20px" }}>
              Вы завершили викторину!
            </Typography>
            <Typography variant="h6" sx={{ marginBottom: "10px" }}>
              Ваши результаты: {quizResults} из {questions.length} правильных
              ответов.
            </Typography>
            <Button
              variant="contained"
              color="primary"
              onClick={() => {
                setQuizResults(0);
                setCurrentQuestionIndex(0);
                setIsQuizFinished(false);
              }}
              sx={{
                marginTop: "20px",
                ":hover": { transform: "scale(1.05)" },
                padding: "12px 20px",
              }}
            >
              Пройти заново
            </Button>
          </Box>
        )}
      </Box>
    </Box>
  );
};

export default MainPage;

