import React from "react";
import videoBg from "../homePage/assets/videoBg.mp4"; // Путь к видео

const MainPage = () => {
  return (
    <div
      className="home-main-page"
      style={{ position: "relative", minHeight: "100vh" }}
    >
      {/* Видео фон */}
      <video
        className="background-video"
        src={videoBg}
        autoPlay
        loop
        muted
        playsInline
        style={{
          position: "absolute",
          top: 0,
          left: 0,
          width: "100%",
          height: "100%",
          objectFit: "cover",
          zIndex: -1, // Убираем под фон
        }}
      />

      {/* Затемнённый контент */}
      <div
        style={{
          position: "relative",
          width: "100%",
          minHeight: "100vh",
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          textAlign: "center",
          color: "white",
          backgroundColor: "rgba(0, 0, 0, 0.5)", // затемнение
        }}
      >
        <div style={{ maxWidth: "800px", padding: "20px" }}>
          <h1
            style={{
              fontSize: "48px",
              marginBottom: "10px",
              fontWeight: "bold",
              fontFamily: "Georgia, serif",
            }}
          >
            Спортсмены Кыргызстана
          </h1>
          <p
            style={{ fontSize: "20px", fontWeight: "bold", lineHeight: "1.4" }}
          >
            Кыргызстан — страна в Центральной Азии, известная своей живописной
            природой, горами Тянь-Шань, кристально чистыми озерами и богатой
            культурой. Здесь проживает около 6,5 миллионов человек, а столица
            страны — Бишкек. В Кыргызстане особенно ценятся традиции,
            гостеприимство и любовь к национальным видам спорта, таким как
            кок-бору и борьба.
          </p>
        </div>
      </div>

      {/* Изображение ниже контента */}
      <div
        style={{
          textAlign: "center",
          padding: "10px",
          backgroundColor: "#1a1a1a",
        }}
      >
        <img
          src="https://data.kaktus.media/image/big/2024-08-11_19-52-39_202865.jpg"
          alt="Кыргызстан"
          style={{ width: "100%", maxWidth: "100px", borderRadius: "10px" }}
        />
      </div>
    </div>
  );
};

export default MainPage;

