import React, { useEffect, useState } from "react";
import { Button, Box, Typography, Card, CardContent } from "@mui/material";
import videoBg from "../homePage/assets/videoBg.mp4";

const quizData = [
  {
    question: "Кто является первым олимпийским чемпионом Кыргызстана?",
    options: [
      "Канатбек Бегалиев",
      "Айдос Султангазиев",
      "Александр Большунов",
      "Юрий Мельниченко",
    ],
    answer: "Юрий Мельниченко",
  },
  {
    question:
      "В каком виде спорта Кыргызстан завоевал свою первую олимпийскую медаль?",
    options: ["Бокс", "Лёгкая атлетика", "Греко-римская борьба", "Дзюдо"],
    answer: "Греко-римская борьба",
  },
  {
    question: "Сколько олимпийских медалей завоевал Кыргызстан за всю историю?",
    options: ["5", "9", "12", "15"],
    answer: "9",
  },
];

const MainPage = () => {
  const [isVisible, setIsVisible] = useState(false);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [selectedAnswer, setSelectedAnswer] = useState(null);
  const [score, setScore] = useState(0);
  const [quizFinished, setQuizFinished] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 200) {
        setIsVisible(true);
      } else {
        setIsVisible(false);
      }
    };
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  const handleAnswerClick = (option) => {
    setSelectedAnswer(option);
    if (option === quizData[currentQuestion].answer) {
      setScore(score + 1);
    }
    setTimeout(() => {
      if (currentQuestion + 1 < quizData.length) {
        setCurrentQuestion(currentQuestion + 1);
        setSelectedAnswer(null);
      } else {
        setQuizFinished(true);
      }
    }, 1000);
  };

  return (
    <Box
      sx={{
        position: "relative",
        minHeight: "100vh",
        overflow: "hidden",
        display: "flex",
        flexDirection: "column",
      }}
    >
      <Box
        sx={{
          position: "absolute",
          top: 0,
          left: 0,
          width: "100%",
          height: "100%",
          zIndex: -1,
        }}
      >
        <video
          src={videoBg}
          autoPlay
          loop
          muted
          playsInline
          style={{
            width: "100%",
            height: "100%",
            objectFit: "cover",
            filter: "brightness(80%)",
          }}
        />
        <div
          style={{
            position: "absolute",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            background:
              "linear-gradient(to bottom, rgba(0,0,0,0.5), rgba(0,0,0,0.9))",
          }}
        ></div>
      </Box>

      <Box
        sx={{
          position: "relative",
          width: "100%",
          minHeight: "100vh",
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          textAlign: "center",
          color: "white",
          padding: "20px",
          opacity: isVisible ? 1 : 0,
          transform: isVisible ? "translateY(0)" : "translateY(30px)",
          transition: "all 0.8s ease-out",
        }}
      >
        <Box sx={{ maxWidth: "800px" }}>
          <Typography
            variant="h1"
            sx={{
              fontSize: "48px",
              fontWeight: "bold",
              fontFamily: "Georgia, serif",
              letterSpacing: "1px",
              marginBottom: "15px",
            }}
          >
            Спортсмены Кыргызстана
          </Typography>
          <Typography
            variant="body1"
            sx={{
              fontSize: "clamp(16px, 2vw, 22px)",
              fontWeight: "500",
              lineHeight: "1.6",
              maxWidth: "90%",
              margin: "0 auto",
            }}
          >
            Кыргызстанские спортсмены добились больших успехов в международных
            соревнованиях, особенно в борьбе, боксе и дзюдо.
          </Typography>
        </Box>
      </Box>

      <Box
        sx={{
          textAlign: "center",
          padding: "40px 20px",
          backgroundColor: "#121212",
        }}
      >
        <Typography
          variant="h4"
          sx={{ color: "#ff9800", marginBottom: "20px" }}
        >
          Викторина про олимпийских чемпионов Кыргызстана
        </Typography>
        <Card
          sx={{
            maxWidth: "600px",
            margin: "0 auto",
            padding: "20px",
            backgroundColor: "#1e1e1e",
            color: "white",
            borderRadius: "12px",
            boxShadow: "0 4px 10px rgba(255, 255, 255, 0.1)",
          }}
        >
          {quizFinished ? (
            <Typography variant="h5">
              Вы завершили викторину! Ваш результат: {score} из{" "}
              {quizData.length}
            </Typography>
          ) : (
            <>
              <CardContent>
                <Typography variant="h6" sx={{ marginBottom: "15px" }}>
                  {quizData[currentQuestion].question}
                </Typography>
                {quizData[currentQuestion].options.map((option, index) => (
                  <Button
                    key={index}
                    variant="contained"
                    sx={{
                      margin: "5px",
                      backgroundColor:
                        selectedAnswer === option
                          ? option === quizData[currentQuestion].answer
                            ? "green"
                            : "red"
                          : "#ff9800",
                      transition: "0.3s",
                    }}
                    onClick={() => handleAnswerClick(option)}
                    disabled={!!selectedAnswer}
                  >
                    {option}
                  </Button>
                ))}
              </CardContent>
            </>
          )}
        </Card>
      </Box>
    </Box>
  );
};

export default MainPage;

