import React from "react";
import { useParams, useNavigate } from "react-router-dom";
import { Typography, Button, Container, Box } from "@mui/material";

const newsData = [
  {
    id: 1,
    title: "Чемпионат Азии по борьбе-2025: у Эрназара Акматалиева - золото!",
    description: "Впервые в своей карьере!!!",
    date: "21:59, 30.03.2025",
    image:
      "https://prosports.kg/upload/news/main//202503/254179_51d2d45eff3b9e8645a242d95de7a921.webp",
    content:
      "Эрназар Акматалиев завоевал первое место на чемпионате Азии по борьбе в 2025 году. Этот успех стал результатом упорных тренировок и настойчивости.\n\nКыргызстанский спортсмен Эрназар Акматалиев стал победителем чемпионата Азии по борьбе в Иордании, сообщает Prosports.kz.\n\nОн выступал 29 марта в весовой категории до 70 кг.\n\nВ 1/8 финала кыргызстанец победил спортсмена из Казахстана (5:4), а затем справился с представителем Узбекистана со счетом 7:1.\n\nАкматалиев вышел в полуфинал, где его соперником стал японский атлет. После первого периода японец вел 5:0, а затем и 6:1, но после этого кыргызстанец смог собраться и вырвал победу.\n\nВ финальной схватке против Виктора Рассадина из Таджикистана Акматалиева снова проигрывал после первого периода, на этот раз со счетом 0:4. Во второй половине схватки кыргызстанец набрал 4 балла и смог вырвать победу.\n\nЭта золотая медаль стала для Акматалиева первой в карьере.",
  },
  {
    id: 2,
    title: "КПЛ-2025: «Мурас Юнайтед» и «Алай» - лидеры после третьего  тура",
    description:
      "«Мурас Юнайтед» и «Алай» делят первое место в КПЛ-2025 после третьего тура.",
    date: "14:01, 30.03.2025",
    image:
      "https://sport.kg/uploads/posts/2025-03/medium/1743321694_ikonka.jpeg",
    content:
      "В третьем туре КПЛ-2025 «Мурас Юнайтед» и «Алай» по-прежнему остаются на первом месте, что делает чемпионат интересным и непредсказуемым.",
  },
];

const NewsDetailsPages = () => {
  const { id } = useParams();
  const newsItem = newsData.find((news) => news.id.toString() === id);
  const navigate = useNavigate();

  if (!newsItem) {
    return <Typography variant="h6">Новость не найдена</Typography>;
  }

  // Разделяем контент новости на абзацы и выравниваем текст по ширине
  const contentParagraphs = newsItem.content
    .split("\n\n")
    .map((para, index) => (
      <Typography
        variant="body1"
        lineHeight={1.8}
        key={index}
        paragraph
        sx={{ textAlign: "justify" }} // Выравнивание по ширине
      >
        {para}
      </Typography>
    ));

  return (
    <Box
      sx={{
        backgroundColor: "#474747", // Чёрный фон на всю страницу
        minHeight: "100vh",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        py: 4,
      }}
    >
      <Container maxWidth="md" sx={{ color: "#fff" }}>
        <Button
          onClick={() => navigate("/news")}
          sx={{
            mb: 3,
            backgroundColor: "#ff4d6d",
            color: "#fff",
            ":hover": { backgroundColor: "#ff1a4d" },
          }}
        >
          Назад к новостям
        </Button>

        {/* Заголовок и дата сверху картинки */}
        <Typography variant="h4" fontWeight="bold" gutterBottom>
          {newsItem.title}
        </Typography>
        <Typography variant="body2" sx={{ color: "#b0b0b0", mb: 2 }}>
          {newsItem.date}
        </Typography>

        <Box
          component="img"
          src={newsItem.image}
          alt={newsItem.title}
          sx={{
            width: "100%",
            borderRadius: "10px",
            boxShadow: "0px 4px 10px rgba(255, 255, 255, 0.2)",
            mb: 2,
          }}
        />
        {contentParagraphs}
      </Container>
    </Box>
  );
};

export default NewsDetailsPages;
