import React, { useEffect, useState } from "react";
import { Button, Box, Typography, Fade, CircularProgress } from "@mui/material";
import videoBg from "../homePage/assets/videoBg.mp4"; // Путь к видео

const MainPage = () => {
  const [isVisible, setIsVisible] = useState(false);
  const [showQuiz, setShowQuiz] = useState(true); // Викторина показывается по умолчанию
  const [selectedOption, setSelectedOption] = useState(null);
  const [quizFeedback, setQuizFeedback] = useState("");
  const [quizResults, setQuizResults] = useState(0);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [isLoading, setIsLoading] = useState(false); // Стейт для загрузки

  const questions = [
    {
      question:
        "Кто из спортсменов Кыргызстана стал олимпийским чемпионом в Токио?",
      options: [
        "Гульнара Бекмаматова",
        "Медет Сайдулаев",
        "Уланбек Амиров",
        "Саиджон Мадалиев",
      ],
      correct: 0,
    },
    {
      question:
        "Какой вид спорта принес Кыргызстану первую олимпийскую медаль?",
      options: ["Бокс", "Борьба", "Дзюдо", "Легкая атлетика"],
      correct: 1,
    },
    {
      question:
        "Сколько медалей завоевали спортсмены Кыргызстана на Олимпиаде 2016 года?",
      options: ["1 медаль", "2 медали", "3 медали", "4 медали"],
      correct: 1,
    },
  ];

  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 200) {
        setIsVisible(true);
      } else {
        setIsVisible(false);
      }
    };
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  const handleOptionClick = (index) => {
    if (selectedOption !== null) return; // Не даем выбрать ответ повторно
    setSelectedOption(index);

    const correctOption = questions[currentQuestionIndex].correct;
    setIsLoading(true); // Показать индикатор загрузки

    setTimeout(() => {
      if (index === correctOption) {
        setQuizFeedback("Правильный ответ!");
        setQuizResults(quizResults + 1);
      } else {
        setQuizFeedback("Неправильный ответ, попробуйте снова.");
      }
      setIsLoading(false); // Скрыть индикатор загрузки
    }, 1000); // Эмулируем задержку для загрузки ответа
  };

  const handleNextQuestion = () => {
    setSelectedOption(null);
    setQuizFeedback("");
    if (currentQuestionIndex < questions.length - 1) {
      setCurrentQuestionIndex(currentQuestionIndex + 1);
    } else {
      setShowQuiz(false); // Закрыть викторину после всех вопросов
    }
  };

  return (
    <Box
      className="home-main-page"
      sx={{
        position: "relative",
        minHeight: "100vh",
        overflow: "hidden",
        display: "flex",
        flexDirection: "column",
      }}
    >
      {/* Видео фон */}
      <Box
        sx={{
          position: "absolute",
          top: 0,
          left: 0,
          width: "100%",
          height: "100%",
          zIndex: -1,
        }}
      >
        <video
          className="background-video"
          src={videoBg}
          autoPlay
          loop
          muted
          playsInline
          style={{
            width: "100%",
            height: "100%",
            objectFit: "cover",
            filter: "brightness(80%)", // Сделал фон темнее для лучшего контраста
          }}
        />
        <div
          style={{
            position: "absolute",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            background:
              "linear-gradient(to bottom, rgba(0,0,0,0.5), rgba(0,0,0,0.9))", // Более темный оверлей
          }}
        ></div>
      </Box>

      {/* Викторина */}
      <Fade in={showQuiz}>
        <Box
          sx={{
            padding: "40px 20px",
            backgroundColor: "#2c2c2c",
            color: "white",
            textAlign: "center",
            marginTop: "30px",
            borderRadius: "10px",
            opacity: isVisible ? 1 : 0,
            transform: isVisible ? "translateY(0)" : "translateY(30px)",
            transition: "all 0.8s ease-out",
          }}
        >
          <Typography
            variant="h2"
            sx={{ marginBottom: "20px", fontWeight: "bold" }}
          >
            Викторина: Олимпийские чемпионы Кыргызстана
          </Typography>
          <Typography variant="h5" sx={{ marginBottom: "20px" }}>
            {questions[currentQuestionIndex].question}
          </Typography>
          <Box sx={{ display: "flex", justifyContent: "center", gap: "15px" }}>
            {questions[currentQuestionIndex].options.map((option, idx) => (
              <Button
                key={idx}
                variant="contained"
                color={selectedOption === idx ? "secondary" : "primary"}
                onClick={() => handleOptionClick(idx)}
                sx={{
                  transition: "all 0.3s ease",
                  ":hover": { transform: "scale(1.05)" },
                  padding: "10px 20px",
                }}
              >
                {option}
              </Button>
            ))}
          </Box>

          {/* Индикатор загрузки */}
          {isLoading && <CircularProgress sx={{ marginTop: "20px" }} />}

          {/* Обратная связь */}
          <Typography sx={{ marginTop: "10px", color: "yellow" }}>
            {quizFeedback}
          </Typography>

          {/* Результаты */}
          {selectedOption !== null && !isLoading && (
            <Typography sx={{ marginTop: "20px", fontWeight: "bold" }}>
              Баллы: {quizResults}
            </Typography>
          )}

          {/* Кнопка для перехода к следующему вопросу */}
          {selectedOption !== null && !isLoading && (
            <Button
              variant="contained"
              color="secondary"
              onClick={handleNextQuestion}
              sx={{
                marginTop: "20px",
                ":hover": { transform: "scale(1.05)" },
                padding: "12px 20px",
              }}
            >
              {currentQuestionIndex < questions.length - 1
                ? "Следующий вопрос"
                : "Завершить викторину"}
            </Button>
          )}
        </Box>
      </Fade>
    </Box>
  );
};

export default MainPage;

