import React, { useState, useEffect } from "react";
import { Button, Box, Typography, Container } from "@mui/material";
import { keyframes } from "@emotion/react"; // Для анимаций

const fadeIn = keyframes`
  from {
    opacity: 0;
  }
  to {
    opacity: 1;
  }
`;

const MainPage = () => {
  const [questionIndex, setQuestionIndex] = useState(0);
  const [score, setScore] = useState(0);
  const [userAnswer, setUserAnswer] = useState("");
  const [isQuizFinished, setIsQuizFinished] = useState(false);

  const questions = [
    {
      question:
        "Какая страна выиграла самые много медалей на Олимпиаде в Токио?",
      options: ["Казахстан", "Кыргызстан", "Россия", "США"],
      correctAnswer: "США",
    },
    {
      question: "Кто из спортсменов Кыргызстана завоевал золото на Олимпиаде?",
      options: [
        "Данияр Ашиханов",
        "Серик Таранов",
        "Айсулу Тыныбекова",
        "Арсен Кудайбергенов",
      ],
      correctAnswer: "Айсулу Тыныбекова",
    },
    {
      question:
        "В каком виде спорта Кыргызстан имеет сильные позиции на Олимпийских играх?",
      options: ["Бокс", "Футбол", "Легкая атлетика", "Гимнастика"],
      correctAnswer: "Бокс",
    },
  ];

  const handleAnswer = (answer) => {
    if (answer === questions[questionIndex].correctAnswer) {
      setScore(score + 1);
    }
    setUserAnswer(answer);
  };

  const handleNextQuestion = () => {
    if (questionIndex + 1 < questions.length) {
      setQuestionIndex(questionIndex + 1);
      setUserAnswer("");
    } else {
      setIsQuizFinished(true);
    }
  };

  return (
    <Container>
      <Box
        sx={{
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          marginTop: "50px",
          padding: "20px",
          animation: `${fadeIn} 1s ease-out`,
        }}
      >
        <Typography
          variant="h3"
          sx={{ marginBottom: "20px", color: "#ff3366" }}
        >
          Викторина: Олимпийские чемпионы Кыргызстана
        </Typography>

        {!isQuizFinished ? (
          <Box
            sx={{
              display: "flex",
              flexDirection: "column",
              alignItems: "center",
              padding: "20px",
              backgroundColor: "#fafafa",
              borderRadius: "12px",
              boxShadow: "0 4px 15px rgba(0, 0, 0, 0.1)",
              transition: "transform 0.3s ease-in-out",
              "&:hover": {
                transform: "scale(1.05)",
              },
              marginBottom: "20px",
            }}
          >
            <Typography
              variant="h5"
              sx={{ marginBottom: "20px", color: "#121212" }}
            >
              {questions[questionIndex].question}
            </Typography>

            <Box
              sx={{
                display: "grid",
                gridTemplateColumns: "1fr 1fr",
                gap: "20px",
                marginBottom: "20px",
              }}
            >
              {questions[questionIndex].options.map((option, index) => (
                <Button
                  key={index}
                  variant="contained"
                  color="primary"
                  onClick={() => handleAnswer(option)}
                  sx={{
                    backgroundColor: "#ff4d6d",
                    "&:hover": {
                      backgroundColor: "#ff3366",
                    },
                    padding: "15px 25px",
                    fontSize: "16px",
                    borderRadius: "8px",
                    boxShadow: "0 4px 15px rgba(0, 0, 0, 0.2)",
                    transition: "all 0.3s ease",
                  }}
                >
                  {option}
                </Button>
              ))}
            </Box>

            {userAnswer && (
              <Typography
                variant="body1"
                sx={{
                  color:
                    userAnswer === questions[questionIndex].correctAnswer
                      ? "green"
                      : "red",
                  fontWeight: "bold",
                  marginBottom: "20px",
                  animation: "fadeIn 0.8s ease-in-out",
                }}
              >
                {userAnswer === questions[questionIndex].correctAnswer
                  ? "Правильный ответ!"
                  : `Неправильный ответ! Правильный ответ: ${questions[questionIndex].correctAnswer}`}
              </Typography>
            )}

            {userAnswer && (
              <Button
                variant="contained"
                color="secondary"
                onClick={handleNextQuestion}
                sx={{
                  backgroundColor: "#4caf50",
                  "&:hover": {
                    backgroundColor: "#45a049",
                  },
                  padding: "10px 20px",
                  borderRadius: "8px",
                  fontSize: "16px",
                  transition: "all 0.3s ease",
                }}
              >
                Следующий вопрос
              </Button>
            )}
          </Box>
        ) : (
          <Box
            sx={{
              display: "flex",
              flexDirection: "column",
              alignItems: "center",
              padding: "20px",
              backgroundColor: "#fafafa",
              borderRadius: "12px",
              boxShadow: "0 4px 15px rgba(0, 0, 0, 0.1)",
              transition: "transform 0.3s ease-in-out",
              "&:hover": {
                transform: "scale(1.05)",
              },
              marginBottom: "20px",
            }}
          >
            <Typography
              variant="h5"
              sx={{ marginBottom: "20px", color: "#121212" }}
            >
              Викторина завершена!
            </Typography>
            <Typography variant="h6" sx={{ color: "#121212" }}>
              Ваши очки: {score} из {questions.length}
            </Typography>
            <Button
              variant="contained"
              color="primary"
              sx={{
                marginTop: "20px",
                padding: "10px 20px",
                borderRadius: "8px",
                fontSize: "16px",
                backgroundColor: "#ff3366",
                "&:hover": {
                  backgroundColor: "#e62e3a",
                },
                transition: "all 0.3s ease",
              }}
              onClick={() => {
                setIsQuizFinished(false);
                setQuestionIndex(0);
                setScore(0);
              }}
            >
              Пройти викторину заново
            </Button>
          </Box>
        )}
      </Box>
    </Container>
  );
};

export default MainPage;

