import React, { useEffect, useState } from "react";
import { Button, Box, Typography, Card, CardContent } from "@mui/material";
import videoBg from "../homePage/assets/videoBg.mp4"; // Путь к видео
import natureKR from "../homePage/assets/natureKR.jpg"; // Путь к фону для викторины

const quizQuestions = [
  {
    question: "Какая столица Кыргызстана?",
    options: ["Ош", "Бишкек", "Талас", "Нарын"],
    correct: 1,
  },
  {
    question: "Какой горный хребет проходит через Кыргызстан?",
    options: ["Памир", "Тянь-Шань", "Урал", "Кавказ"],
    correct: 1,
  },
  {
    question: "Какой язык является официальным в Кыргызстане?",
    options: ["Русский", "Кыргызский", "Таджикский", "Казахский"],
    correct: 1,
  },
  {
    question: "Когда Кыргызстан стал независимым?",
    options: ["1990", "1991", "1992", "1993"],
    correct: 1,
  },
  {
    question: "Какая основная религия в Кыргызстане?",
    options: ["Ислам", "Христианство", "Буддизм", "Древние верования"],
    correct: 0,
  },
  {
    question: "Кто является первым президентом Кыргызстана?",
    options: [
      "Сапар Исаков",
      "Аскар Акаев",
      "Эрмек Молдованов",
      "Роза Отунбаева",
    ],
    correct: 1,
  },
  {
    question: "Как называется национальное блюдо Кыргызстана?",
    options: ["Плов", "Бешбармак", "Курт", "Лагман"],
    correct: 0,
  },
  {
    question: "В каком году Кыргызстан присоединился к ООН?",
    options: ["1992", "1995", "1998", "2000"],
    correct: 0,
  },
  {
    question: "Какой олимпийский спорт популярен в Кыргызстане?",
    options: ["Бокс", "Футбол", "Легкая атлетика", "Баскетбол"],
    correct: 0,
  },
  {
    question: "Какая столица Кыргызстана до независимости?",
    options: ["Ош", "Фрунзе", "Ташкент", "Алма-Ата"],
    correct: 1,
  },
];

const MainPage = () => {
  const [isVisible, setIsVisible] = useState(false);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [score, setScore] = useState(0);
  const [isAnswered, setIsAnswered] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 200) {
        setIsVisible(true);
      } else {
        setIsVisible(false);
      }
    };
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  const handleAnswer = (index) => {
    if (index === quizQuestions[currentQuestion].correct) {
      setScore(score + 1);
    }
    setIsAnswered(true);
  };

  const nextQuestion = () => {
    setIsAnswered(false);
    if (currentQuestion < quizQuestions.length - 1) {
      setCurrentQuestion(currentQuestion + 1);
    } else {
      alert(
        `Викторина завершена! Ваш результат: ${score} из ${quizQuestions.length}`
      );
    }
  };

  return (
    <Box
      className="home-main-page"
      sx={{
        position: "relative",
        minHeight: "100vh",
        overflow: "hidden",
        display: "flex",
        flexDirection: "column",
      }}
    >
      {/* Видео фон */}
      <Box
        sx={{
          position: "absolute",
          top: 0,
          left: 0,
          width: "100%",
          height: "100%",
          zIndex: -1,
        }}
      >
        <video
          className="background-video"
          src={videoBg}
          autoPlay
          loop
          muted
          playsInline
          style={{
            width: "100%",
            height: "100%",
            objectFit: "cover",
            filter: "brightness(80%)", // Сделал фон темнее для лучшего контраста
          }}
        />
        <div
          style={{
            position: "absolute",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            background:
              "linear-gradient(to bottom, rgba(0,0,0,0.5), rgba(0,0,0,0.9))", // Более темный оверлей
          }}
        ></div>
      </Box>

      {/* Контент с анимацией */}
      <Box
        sx={{
          position: "relative",
          width: "100%",
          minHeight: "100vh",
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          textAlign: "center",
          color: "white",
          padding: "20px",
          opacity: isVisible ? 1 : 0,
          transform: isVisible ? "translateY(0)" : "translateY(30px)", // Меньше движения
          transition: "all 0.8s ease-out",
        }}
      >
        <Box sx={{ maxWidth: "800px" }}>
          <Typography
            variant="h1"
            sx={{
              fontSize: "48px",
              marginBottom: "15px",
              fontWeight: "bold",
              fontFamily: "Georgia, serif",
              letterSpacing: "1px",
            }}
          >
            Спортсмены Кыргызстана
          </Typography>
          <Typography
            variant="body1"
            sx={{
              fontSize: "clamp(16px, 2vw, 22px)",
              fontWeight: "500",
              lineHeight: "1.6",
              maxWidth: "90%",
              margin: "0 auto",
            }}
          >
            Спортсмены Кыргызстана завоевывают признание на международной арене
            в таких видах спорта, как борьба, дзюдо, бокс и легкая атлетика. Эти
            атлеты становятся символами силы, упорства и решимости. Их
            достижения вдохновляют новое поколение, показывая, что стремление к
            победе и трудолюбие открывают путь к вершинам.
          </Typography>
        </Box>
      </Box>

      {/* Блок с изображением с эффектом параллакса */}
      <Box
        sx={{
          textAlign: "center",
          padding: "20px",
          backgroundColor: "#121212",
          display: "flex",
          justifyContent: "center",
          position: "relative",
        }}
      >
        <img
          src="https://data.kaktus.media/image/big/2024-08-11_19-52-39_202865.jpg"
          alt="Кыргызстан"
          style={{
            width: "90%",
            maxWidth: "1400px",
            borderRadius: "12px",
            boxShadow: "0 4px 15px rgba(255, 255, 255, 0.1)",
            transition: "transform 0.3s ease, filter 0.3s ease",
            transform: "scale(1)",
            filter: "brightness(80%)",
          }}
          onMouseOver={(e) => {
            e.target.style.transform = "scale(1.03)";
            e.target.style.filter = "brightness(100%)";
          }}
          onMouseOut={(e) => {
            e.target.style.transform = "scale(1)";
            e.target.style.filter = "brightness(80%)";
          }}
        />
      </Box>

      {/* Викторина */}
      <Box
        sx={{
          backgroundImage: `url(${natureKR})`,
          backgroundSize: "cover",
          padding: "50px 20px",
          color: "white",
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          flexDirection: "column",
          minHeight: "50vh",
        }}
      >
        <Typography
          variant="h2"
          sx={{
            fontSize: "32px",
            fontWeight: "bold",
            marginBottom: "20px",
            textAlign: "center",
          }}
        >
          Викторина о Кыргызстане
        </Typography>
        <Card
          sx={{
            width: "100%",
            maxWidth: "600px",
            marginBottom: "20px",
            boxShadow: "0 4px 15px rgba(255, 255, 255, 0.1)",
            opacity: isAnswered ? 0.5 : 1,
            transition: "all 0.3s ease",
          }}
        >
          <CardContent>
            <Typography variant="h5" sx={{ marginBottom: "15px" }}>
              {quizQuestions[currentQuestion].question}
            </Typography>
            {quizQuestions[currentQuestion].options.map((option, index) => (
              <Button
                key={index}
                onClick={() => handleAnswer(index)}
                sx={{
                  display: "block",
                  margin: "10px auto",
                  backgroundColor: "rgba(255, 255, 255, 0.3)",
                  color: "white",
                  "&:hover": { backgroundColor: "rgba(255, 255, 255, 0.5)" },
                }}
              >
                {option}
              </Button>
            ))}
          </CardContent>
        </Card>

        {isAnswered && (
          <Button
            onClick={nextQuestion}
            sx={{
              backgroundColor: "#ff4d6d",
              color: "white",
              padding: "10px 20px",
              borderRadius: "8px",
              "&:hover": { backgroundColor: "#ff1f4d" },
            }}
          >
            Следующий вопрос
          </Button>
        )}
      </Box>
    </Box>
  );
};

export default MainPage;

