import React, { useState } from "react";
import {
  Card,
  CardContent,
  CardMedia,
  Typography,
  Grid,
  Button,
} from "@mui/material";
import { styled } from "@mui/system";

const StyledCard = styled(Card)({
  marginBottom: "20px",
  borderRadius: "10px",
  boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
  transition: "transform 0.3s, box-shadow 0.3s",
  "&:hover": {
    transform: "scale(1.05)",
    boxShadow: "0 8px 16px rgba(0, 0, 0, 0.2)",
  },
});

const Title = styled(Typography)({
  fontWeight: "bold",
  fontSize: "1.2rem",
  color: "#333",
});

const DateText = styled(Typography)({
  color: "#888",
  fontSize: "0.9rem",
  marginBottom: "8px",
});

const ReadMoreButton = styled(Button)({
  backgroundColor: "#ff4d6d",
  color: "#fff",
  padding: "8px 16px",
  borderRadius: "20px",
  "&:hover": {
    backgroundColor: "#ff1a47",
  },
});

const placeholderImage = "https://via.placeholder.com/300";

const NewsCard = ({ news }) => {
  const [imgSrc, setImgSrc] = useState(news.image);

  return (
    <StyledCard>
      <CardMedia
        component="img"
        height="200"
        image={imgSrc}
        alt={news.title}
        onError={() => setImgSrc(placeholderImage)}
        sx={{
          width: "100%", // ширина на весь экран
          height: "300px", // высота на весь экран
          // objectFit: "none", // или "contain", если нужно видеть все изображение
        }} // Или попробуйте "contain"
      />
      <CardContent>
        <Title>{news.title}</Title>
        <DateText>{news.date}</DateText>
        {/* Добавим описание */}
        {news.description && (
          <Typography variant="body2" color="textSecondary" paragraph>
            {news.description}
          </Typography>
        )}
        {!!news.link && (
          <ReadMoreButton
            component="a"
            href={news.link}
            target="_blank"
            rel="noopener noreferrer"
            sx={{ textDecoration: "none" }}
          >
            Читать далее
          </ReadMoreButton>
        )}
      </CardContent>
    </StyledCard>
  );
};

const newsData = [
  {
    id: 1,
    title: "Чемпионат Азии по борьбе-2025: у Эрназара Акматалиева - золото!",
    description: "Впервые в своей карьере!!!",
    date: "30.03.2025 21:59",
    image:
      "https://prosports.kg/upload/news/main//202503/254179_51d2d45eff3b9e8645a242d95de7a921.webp",
    link: "https://sport.kg/news/akmataliev-gold",
  },
  {
    id: 2,
    title: "КПЛ-2025: «Мурас Юнайтед» и «Алай» - лидеры после третьего  тура",
    description:
      "«Мурас Юнайтед» и «Алай» делят первое место в КПЛ-2025 после третьего тура.",
    date: "30.03.2025",
    image:
      "https://sport.kg/uploads/posts/2025-03/medium/1743321694_ikonka.jpeg",
  },
  {
    id: 3,
    title: "Кыргызстанские футболисты готовятся к Кубку Азии 2025",
    date: "12 декабря 2024",
    image: "https://via.placeholder.com/300", // Заглушка
  },
  {
    id: 4,
    title: "Кыргызстанские футболисты готовятся к Кубку Азии 2025",
    date: "12 декабря 2024",
    image: "https://via.placeholder.com/300", // Заглушка
  },
];

const NewsPage = () => {
  return (
    <div style={{ padding: "20px", backgroundColor: "#f4f4f4" }}>
      <Typography
        variant="h4"
        align="center"
        gutterBottom
        style={{ marginBottom: "40px" }}
      >
        Новости о спортсменах Кыргызстана (2024-2025)
      </Typography>
      <Grid container spacing={3} justifyContent="center">
        {newsData.map((news) => (
          <Grid item xs={12} md={6} key={news.id}>
            <NewsCard news={news} />
          </Grid>
        ))}
      </Grid>
    </div>
  );
};

export default NewsPage;
