import React, { useEffect, useState } from "react";
import { Button, Box, Typography } from "@mui/material";
import videoBg from "../homePage/assets/videoBg.mp4"; // Путь к видео

const MainPage = () => {
  const [isVisible, setIsVisible] = useState(false);
  const [showQuiz, setShowQuiz] = useState(false);
  const [selectedOption, setSelectedOption] = useState(null);
  const [quizFeedback, setQuizFeedback] = useState("");
  const [quizResults, setQuizResults] = useState(0);
  const [currentQuestion, setCurrentQuestion] = useState(0); // Tracking the current question

  const questions = [
    {
      question:
        "Кто из спортсменов Кыргызстана стал олимпийским чемпионом в Токио?",
      options: [
        "Гульнара Бекмаматова",
        "Медет Сайдулаев",
        "Уланбек Амиров",
        "Саиджон Мадалиев",
      ],
      correct: 0,
    },
    {
      question:
        "Какой вид спорта принес Кыргызстану первую олимпийскую медаль?",
      options: ["Бокс", "Борьба", "Дзюдо", "Легкая атлетика"],
      correct: 1,
    },
    {
      question:
        "Сколько медалей завоевали спортсмены Кыргызстана на Олимпиаде 2016 года?",
      options: ["1 медаль", "2 медали", "3 медали", "4 медали"],
      correct: 1,
    },
  ];

  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 200) {
        setIsVisible(true);
      } else {
        setIsVisible(false);
      }
    };
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  const handleOptionClick = (index) => {
    setSelectedOption(index);
    const correctOption = questions[currentQuestion].correct;
    if (index === correctOption) {
      setQuizFeedback("Правильный ответ!");
      setQuizResults(quizResults + 1);
      // Simulate fireworks effect for correct answer
      setTimeout(() => alert("✨✨ Салют! Правильный ответ! ✨✨"), 500);
    } else {
      setQuizFeedback("Неправильный ответ, попробуйте снова.");
    }
  };

  const handleNextQuestion = () => {
    setSelectedOption(null);
    setQuizFeedback("");
    setCurrentQuestion(currentQuestion + 1);
  };

  const handleFinishQuiz = () => {
    setShowQuiz(false);
    setTimeout(
      () =>
        alert(
          `Викторина завершена! Ваш результат: ${quizResults} из ${questions.length}`
        ),
      500
    );
  };

  return (
    <Box
      className="home-main-page"
      sx={{
        position: "relative",
        minHeight: "100vh",
        overflow: "hidden",
        display: "flex",
        flexDirection: "column",
      }}
    >
      {/* Видео фон */}
      <Box
        sx={{
          position: "absolute",
          top: 0,
          left: 0,
          width: "100%",
          height: "100%",
          zIndex: -1,
        }}
      >
        <video
          className="background-video"
          src={videoBg}
          autoPlay
          loop
          muted
          playsInline
          style={{
            width: "100%",
            height: "100%",
            objectFit: "cover",
            filter: "brightness(80%)", // Сделал фон темнее для лучшего контраста
          }}
        />
        <div
          style={{
            position: "absolute",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            background:
              "linear-gradient(to bottom, rgba(0,0,0,0.5), rgba(0,0,0,0.9))", // Более темный оверлей
          }}
        ></div>
      </Box>

      {/* Викторина */}
      <Box
        sx={{
          padding: "40px 20px",
          backgroundColor: "#2c2c2c",
          color: "white",
          textAlign: "center",
          marginTop: "30px",
          borderRadius: "10px",
          boxShadow: "0 4px 15px rgba(255, 255, 255, 0.1)",
        }}
      >
        <Typography
          variant="h2"
          sx={{
            marginBottom: "20px",
            fontWeight: "bold",
            color: "#ffcc00",
            fontSize: "28px",
            letterSpacing: "2px",
            textTransform: "uppercase",
            animation: "fadeIn 1s ease-in-out",
          }}
        >
          Викторина: Олимпийские чемпионы Кыргызстана
        </Typography>

        {/* Display only the current question */}
        <Box sx={{ marginBottom: "30px", position: "relative" }}>
          <Typography
            variant="h5"
            sx={{
              marginBottom: "15px",
              fontSize: "22px",
              fontWeight: "600",
              textAlign: "center",
              color: "#fff",
              animation: "fadeInUp 0.5s ease-out",
            }}
          >
            {questions[currentQuestion]?.question}
          </Typography>
          <Box
            sx={{
              display: "flex",
              flexDirection: "column",
              gap: "20px",
              justifyContent: "center",
              alignItems: "center",
            }}
          >
            {questions[currentQuestion]?.options.map((option, idx) => (
              <Button
                key={idx}
                variant="contained"
                color={selectedOption === idx ? "success" : "primary"}
                onClick={() => handleOptionClick(idx)}
                sx={{
                  background:
                    selectedOption === idx
                      ? "linear-gradient(135deg, #ff4d6d, #ff9100)"
                      : "#007bff",
                  transition: "all 0.3s ease",
                  ":hover": {
                    transform: "scale(1.1)",
                    background: "#ff4d6d",
                  },
                  padding: "15px 30px",
                  borderRadius: "10px",
                  fontSize: "16px",
                  boxShadow: "0 4px 10px rgba(0,0,0,0.3)",
                  width: "80%",
                }}
              >
                {option}
              </Button>
            ))}
          </Box>
          <Typography
            sx={{
              marginTop: "10px",
              fontWeight: "bold",
              color: quizFeedback === "Правильный ответ!" ? "green" : "red",
              animation: "fadeIn 0.5s ease-out",
            }}
          >
            {quizFeedback}
          </Typography>
        </Box>

        {/* Buttons */}
        {currentQuestion < questions.length - 1 ? (
          <Button
            variant="contained"
            color="secondary"
            onClick={handleNextQuestion}
            sx={{
              marginTop: "30px",
              padding: "12px 20px",
              fontSize: "18px",
              borderRadius: "10px",
              boxShadow: "0 4px 10px rgba(0,0,0,0.3)",
              transition: "transform 0.3s ease",
              ":hover": { transform: "scale(1.05)" },
            }}
          >
            Следующий вопрос
          </Button>
        ) : (
          <Button
            variant="contained"
            color="secondary"
            onClick={handleFinishQuiz}
            sx={{
              marginTop: "30px",
              padding: "12px 20px",
              fontSize: "18px",
              borderRadius: "10px",
              boxShadow: "0 4px 10px rgba(0,0,0,0.3)",
              transition: "transform 0.3s ease",
              ":hover": { transform: "scale(1.05)" },
            }}
          >
            Завершить викторину
          </Button>
        )}
      </Box>
    </Box>
  );
};

export default MainPage;

