import React, { useState, useEffect } from "react";
import { Button, Box, Typography, TextField, Grid, Fade } from "@mui/material";
import videoBg from "../homePage/assets/videoBg.mp4"; // Путь к видео

const MainPage = () => {
  const [isVisible, setIsVisible] = useState(false);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [userAnswer, setUserAnswer] = useState("");
  const [score, setScore] = useState(0);
  const [quizFinished, setQuizFinished] = useState(false);

  const questions = [
    {
      question: "Кто выиграл золото в борьбе на Олимпиаде 2016 в Рио?",
      options: ["Айсулуу Тыныбекова", "Жаныбек Кулубаев", "Данияр Кенжебеков"],
      answer: "Айсулуу Тыныбекова",
    },
    {
      question:
        "В каком виде спорта Кыргызстан завоевал золото на Олимпиаде 2020?",
      options: ["Бокс", "Дзюдо", "Тяжелая атлетика"],
      answer: "Бокс",
    },
    {
      question: "Кто был первым олимпийским чемпионом Кыргызстана?",
      options: ["Талант Ошоев", "Руслан Чойбеков", "Бекболат Туймабаев"],
      answer: "Талант Ошоев",
    },
  ];

  const handleAnswerChange = (e) => {
    setUserAnswer(e.target.value);
  };

  const handleNextQuestion = () => {
    if (userAnswer === questions[currentQuestionIndex].answer) {
      setScore(score + 1);
    }
    setUserAnswer("");
    if (currentQuestionIndex < questions.length - 1) {
      setCurrentQuestionIndex(currentQuestionIndex + 1);
    } else {
      setQuizFinished(true);
    }
  };

  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 200) {
        setIsVisible(true);
      } else {
        setIsVisible(false);
      }
    };
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <Box
      className="home-main-page"
      sx={{
        position: "relative",
        minHeight: "100vh",
        overflow: "hidden",
        display: "flex",
        flexDirection: "column",
      }}
    >
      {/* Видео фон */}
      <Box
        sx={{
          position: "absolute",
          top: 0,
          left: 0,
          width: "100%",
          height: "100%",
          zIndex: -1,
        }}
      >
        <video
          className="background-video"
          src={videoBg}
          autoPlay
          loop
          muted
          playsInline
          style={{
            width: "100%",
            height: "100%",
            objectFit: "cover",
            filter: "brightness(80%)",
          }}
        />
        <div
          style={{
            position: "absolute",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            background:
              "linear-gradient(to bottom, rgba(0,0,0,0.5), rgba(0,0,0,0.9))",
          }}
        ></div>
      </Box>

      {/* Контент с анимацией */}
      <Box
        sx={{
          position: "relative",
          width: "100%",
          minHeight: "100vh",
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          textAlign: "center",
          color: "white",
          padding: "20px",
          opacity: isVisible ? 1 : 0,
          transform: isVisible ? "translateY(0)" : "translateY(30px)",
          transition: "all 0.8s ease-out",
        }}
      >
        <Box sx={{ maxWidth: "800px" }}>
          <Typography
            variant="h1"
            sx={{
              fontSize: "48px",
              marginBottom: "15px",
              fontWeight: "bold",
              fontFamily: "Georgia, serif",
              letterSpacing: "1px",
            }}
          >
            Викторина о Олимпийских чемпионах Кыргызстана
          </Typography>
          <Typography
            variant="body1"
            sx={{
              fontSize: "clamp(16px, 2vw, 22px)",
              fontWeight: "500",
              lineHeight: "1.6",
              maxWidth: "90%",
              margin: "0 auto",
              marginBottom: "30px",
            }}
          >
            Проверьте свои знания о великих спортсменах Кыргызстана, которые
            завоевали олимпийские медали!
          </Typography>
        </Box>
      </Box>

      {/* Викторина */}
      <Fade in={!quizFinished} timeout={1000}>
        <Box
          sx={{
            display: "flex",
            flexDirection: "column",
            justifyContent: "center",
            alignItems: "center",
            backgroundColor: "#121212",
            padding: "40px",
            borderRadius: "8px",
            boxShadow: "0 4px 15px rgba(255, 255, 255, 0.1)",
            margin: "0 auto",
            maxWidth: "800px",
          }}
        >
          <Typography variant="h3" sx={{ marginBottom: "20px" }}>
            {questions[currentQuestionIndex].question}
          </Typography>
          <Grid container spacing={3} sx={{ marginBottom: "30px" }}>
            {questions[currentQuestionIndex].options.map((option, index) => (
              <Grid item xs={12} sm={6} key={index}>
                <Button
                  variant="contained"
                  fullWidth
                  sx={{
                    backgroundColor: "#ff4d6d",
                    color: "white",
                    transition: "all 0.3s ease",
                    "&:hover": {
                      backgroundColor: "#ff3366",
                    },
                  }}
                  onClick={() => setUserAnswer(option)}
                >
                  {option}
                </Button>
              </Grid>
            ))}
          </Grid>
          <Button
            variant="contained"
            sx={{
              backgroundColor: "#28a745",
              color: "white",
              transition: "all 0.3s ease",
              "&:hover": {
                backgroundColor: "#218838",
              },
            }}
            onClick={handleNextQuestion}
          >
            Следующий вопрос
          </Button>
        </Box>
      </Fade>

      {/* Результаты викторины */}
      <Fade in={quizFinished} timeout={1000}>
        <Box
          sx={{
            display: "flex",
            flexDirection: "column",
            justifyContent: "center",
            alignItems: "center",
            backgroundColor: "#121212",
            padding: "40px",
            borderRadius: "8px",
            boxShadow: "0 4px 15px rgba(255, 255, 255, 0.1)",
            margin: "0 auto",
            maxWidth: "800px",
          }}
        >
          <Typography variant="h4" sx={{ marginBottom: "20px" }}>
            Викторина завершена!
          </Typography>
          <Typography variant="h6" sx={{ marginBottom: "20px" }}>
            Ваш результат: {score} из {questions.length}
          </Typography>
          <Button
            variant="contained"
            sx={{
              backgroundColor: "#ff4d6d",
              color: "white",
              transition: "all 0.3s ease",
              "&:hover": {
                backgroundColor: "#ff3366",
              },
            }}
            onClick={() => {
              setScore(0);
              setCurrentQuestionIndex(0);
              setQuizFinished(false);
            }}
          >
            Пройти заново
          </Button>
        </Box>
      </Fade>
    </Box>
  );
};

export default MainPage;

