import React, { useEffect, useState, useCallback } from "react";
import {
  Button,
  Box,
  Typography,
  Card,
  CardContent,
  CardActions,
} from "@mui/material";
import videoBg from "../homePage/assets/videoBg.mp4"; // Путь к видео
import Flag_of_the_KR from "../homePage/assets/Flag_of_the_KR.mp4"; // Путь к видео

const MainPage = () => {
  const [isVisible, setIsVisible] = useState(false);
  const [showQuiz, setShowQuiz] = useState(false);
  const [selectedOption, setSelectedOption] = useState(null);
  const [quizFeedback, setQuizFeedback] = useState("");
  const [quizResults, setQuizResults] = useState(0);

  const questions = [
    {
      question:
        "Кто из спортсменов Кыргызстана стал олимпийским чемпионом в Токио?",
      options: [
        "Гульнара Бекмаматова",
        "Медет Сайдулаев",
        "Уланбек Амиров",
        "Саиджон Мадалиев",
      ],
      correct: 0,
    },
    {
      question:
        "Какой вид спорта принес Кыргызстану первую олимпийскую медаль?",
      options: ["Бокс", "Борьба", "Дзюдо", "Легкая атлетика"],
      correct: 1,
    },
    {
      question:
        "Сколько медалей завоевали спортсмены Кыргызстана на Олимпиаде 2016 года?",
      options: ["1 медаль", "2 медали", "3 медали", "4 медали"],
      correct: 1,
    },
    {
      question: "Кто стал первым олимпийским чемпионом Кыргызстана?",
      options: [
        "Джамбулат Тедeeев",
        "Айсулуу Тыныбекова",
        "Медет Сайдулаев",
        "Саиджон Мадалиев",
      ],
      correct: 0,
    },
    {
      question: "Какой спортсмен Кыргызстана выиграл медаль в Токио 2020?",
      options: [
        "Гульнара Бекмаматова",
        "Уланбек Амиров",
        "Бекжан Исраилов",
        "Тимур Кулов",
      ],
      correct: 1,
    },
    {
      question:
        "Какой спортсмен Кыргызстана получил серебряную медаль в 2016 году?",
      options: [
        "Медет Сайдулаев",
        "Айсулуу Тыныбекова",
        "Гульнара Бекмаматова",
        "Саиджон Мадалиев",
      ],
      correct: 1,
    },
    {
      question: "Какая страна провела летние Олимпийские игры в 2021 году?",
      options: ["Япония", "Южная Корея", "Франция", "Бразилия"],
      correct: 0,
    },
    {
      question: "Какой вид спорта в Кыргызстане получил большую популярность?",
      options: ["Борьба", "Футбол", "Баскетбол", "Дзюдо"],
      correct: 0,
    },
    {
      question: "Какая медаль является самой ценной на Олимпиаде?",
      options: ["Золотая", "Серебряная", "Бронзовая", "Диплом"],
      correct: 0,
    },
    {
      question: "Сколько стран участвуют в Олимпийских играх?",
      options: ["200", "220", "195", "185"],
      correct: 1,
    },
  ];

  const handleScroll = useCallback(() => {
    if (window.scrollY > 200) {
      setIsVisible(true);
    } else {
      setIsVisible(false);
    }
  }, []);

  useEffect(() => {
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, [handleScroll]);

  const [currentQuestion, setCurrentQuestion] = useState(0);

  const handleOptionClick = (index) => {
    setSelectedOption(index);
    const correctOption = questions[currentQuestion].correct;
    if (index === correctOption) {
      setQuizFeedback("Правильный ответ!");
      setQuizResults((prev) => prev + 1);
    } else {
      setQuizFeedback("Неправильный ответ, попробуйте снова.");
    }
  };

  const handleNextQuestion = () => {
    if (selectedOption === null) return; // Запрещаем переход без ответа
    if (currentQuestion < questions.length - 1) {
      setCurrentQuestion(currentQuestion + 1);
      setSelectedOption(null);
      setQuizFeedback("");
    } else {
      setShowQuiz(true); // Показываем результаты после последнего вопроса
    }
  };

  const handleRestartQuiz = () => {
    setCurrentQuestion(0);
    setQuizResults(0);
    setShowQuiz(false);
    setSelectedOption(null);
    setQuizFeedback("");
  };

  return (
    <Box
      className="home-main-page"
      sx={{
        position: "relative",
        minHeight: "100vh",
        overflow: "hidden",
        display: "flex",
        flexDirection: "column",
      }}
    >
      {/* Видео фон */}
      <Box
        sx={{
          position: "absolute",
          top: 0,
          left: 0,
          width: "100%",
          height: "100%",
          zIndex: -1,
        }}
      >
        <video
          className="background-video"
          src={videoBg}
          autoPlay
          loop
          muted
          playsInline
          style={{
            width: "100%",
            height: "100%",
            objectFit: "cover",
            filter: "brightness(80%)", // Сделал фон темнее для лучшего контраста
          }}
        />
        <div
          style={{
            position: "absolute",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            background:
              "linear-gradient(to bottom, rgba(0,0,0,0.5), rgba(0,0,0,0.9))", // Более темный оверлей
          }}
        ></div>
      </Box>

      {/* Контент с анимацией */}
      <Box
        sx={{
          position: "relative",
          width: "100%",
          minHeight: "100vh",
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          textAlign: "center",
          color: "white",
          padding: "20px",
          opacity: isVisible ? 1 : 0,
          transform: isVisible ? "translateY(0)" : "translateY(30px)", // Меньше движения
          transition: "all 0.8s ease-out",
        }}
      >
        <Box sx={{ maxWidth: "800px" }}>
          <Typography
            variant="h1"
            sx={{
              fontSize: "48px",
              marginBottom: "15px",
              fontWeight: "bold",
              fontFamily: "Georgia, serif",
              letterSpacing: "1px",
            }}
          >
            Спортсмены Кыргызстана
          </Typography>
          <Typography
            variant="body1"
            sx={{
              fontSize: "clamp(16px, 2vw, 22px)",
              fontWeight: "500",
              lineHeight: "1.6",
              maxWidth: "90%",
              margin: "0 auto",
            }}
          >
            Спортсмены Кыргызстана завоевывают признание на международной арене
            в таких видах спорта, как борьба, дзюдо, бокс и легкая атлетика. Эти
            атлеты становятся символами силы, упорства и решимости. Их
            достижения вдохновляют новое поколение, показывая, что стремление к
            победе и трудолюбие открывают путь к вершинам.
          </Typography>
        </Box>
      </Box>

      {/* Викторина с фоновым видео */}
      <Box
        sx={{
          position: "relative",
          padding: "40px 20px",
          color: "white",
          textAlign: "center",
          marginTop: "30px",
          borderRadius: "10px",
          overflow: "hidden",
          boxShadow: "0 4px 15px rgba(255, 255, 255, 0.1)",
        }}
      >
        {/* Видео фон */}
        <video
          src={Flag_of_the_KR} // Используем твой видеофайл
          autoPlay
          loop
          muted
          playsInline
          style={{
            position: "absolute",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            objectFit: "cover",
            zIndex: -1,
            filter: "brightness(50%)", // Затемняем видео для читаемости текста
          }}
        />

        {/* Затемняющий оверлей */}
        <Box
          sx={{
            position: "absolute",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            background: "rgba(0, 0, 0, 0.5)", // Полупрозрачный черный фон
            zIndex: -1,
          }}
        ></Box>

        {/* Контент викторины */}
        <Typography
          variant="h2"
          sx={{
            marginBottom: "20px",
            fontWeight: "bold",
            color: "#ffcc00",
            fontSize: "28px",
            letterSpacing: "2px",
            textTransform: "uppercase",
          }}
        >
          Викторина: Олимпийские чемпионы Кыргызстана
        </Typography>

        {showQuiz ? (
          <Typography
            sx={{
              marginTop: "20px",
              fontSize: "24px",
              fontWeight: "bold",
              color: quizResults === questions.length ? "green" : "red",
            }}
          >
            Результат: {quizResults} из {questions.length} правильных ответов
          </Typography>
        ) : (
          <Box>
            <Card
              sx={{
                maxWidth: 400,
                margin: "20px auto",
                padding: "20px",
                background:
                  "radial-gradient(circle, rgba(255, 255, 255, 0.3), rgba(0, 0, 0, 0.1))",
              }}
            >
              <CardContent>
                <Typography
                  variant="h5"
                  sx={{ fontSize: "22px", fontWeight: "600" }}
                >
                  {questions[currentQuestion].question}
                </Typography>
                <Box sx={{ marginTop: "20px" }}>
                  {questions[currentQuestion].options.map((option, index) => (
                    <Button
                      key={index}
                      fullWidth
                      variant="outlined"
                      sx={{
                        marginBottom: "10px",
                        backgroundColor:
                          selectedOption === index ? "#ffcc00" : "transparent",
                        color: selectedOption === index ? "black" : "white",
                        "&:hover": {
                          backgroundColor:
                            selectedOption === index ? "#ffcc00" : "#333",
                        },
                      }}
                      onClick={() => handleOptionClick(index)}
                    >
                      {option}
                    </Button>
                  ))}
                </Box>
                <Typography sx={{ marginTop: "20px" }}>
                  {quizFeedback}
                </Typography>
              </CardContent>
              <CardActions>
                <Button
                  fullWidth
                  variant="contained"
                  color="primary"
                  onClick={handleNextQuestion}
                  disabled={selectedOption === null}
                  sx={{
                    backgroundColor: "#ffcc00",
                    "&:hover": {
                      backgroundColor: "#ff9900",
                    },
                  }}
                >
                  Следующий вопрос
                </Button>
              </CardActions>
            </Card>
          </Box>
        )}

        {showQuiz && (
          <Box sx={{ marginTop: "20px" }}>
            <Button
              variant="outlined"
              color="secondary"
              onClick={handleRestartQuiz}
              sx={{
                backgroundColor: "#ff4d6d",
                "&:hover": {
                  backgroundColor: "#ff1f40",
                },
              }}
            >
              Начать заново
            </Button>
          </Box>
        )}
      </Box>
    </Box>
  );
};

export default MainPage;

