import React, { createContext, useState, useEffect, useContext } from "react";
import axios from "axios";

// Создаем контекст продуктов
const ProductContext = createContext();

// Кастомный хук для использования контекста продуктов
export const useProduct = () => useContext(ProductContext);

// Компонент провайдера контекста продуктов
const ProductContextProvider = ({ children }) => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);

  const fetchProducts = async () => {
    setLoading(true); // Начинаем загрузку данных
    try {
      const response = await axios.get("http://localhost:8000/products");
      setProducts(response.data); // Устанавливаем данные продуктов
    } catch (error) {
      console.error("Ошибка при загрузке продуктов:", error);
    } finally {
      setLoading(false); // Завершаем загрузку
    }
  };

  useEffect(() => {
    fetchProducts(); // Загружаем продукты только один раз при монтировании компонента
  }, []); // Пустой массив зависимостей гарантирует выполнение только один раз

  return (
    <ProductContext.Provider value={{ products, loading }}>
      {children}
    </ProductContext.Provider>
  );
};

export default ProductContextProvider;
