import React, { useEffect, useState } from "react";
import { Button, Box, Typography, Card } from "@mui/material";
import Confetti from "react-confetti";
import videoBg from "../homePage/assets/videoBg.mp4";
import natureKR from "../homePage/assets/natureKR.mp4";

const MainPage = () => {
  const [isVisible, setIsVisible] = useState(false);
  const [selectedAnswer, setSelectedAnswer] = useState(null);
  const [isCorrect, setIsCorrect] = useState(null);
  const [showConfetti, setShowConfetti] = useState(false);
  const [isWrongAnswer, setIsWrongAnswer] = useState(false);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);

  useEffect(() => {
    const handleScroll = () => {
      setIsVisible(window.scrollY > 200);
    };
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  const questions = [
    {
      question: "Какой самый высокий пик в Кыргызстане?",
      answers: ["Пик Ленина", "Пик Победы", "Пик Манас", "Пик Корона"],
      correctAnswer: "Пик Победы",
    },
    {
      question: "Какое национальное блюдо кыргызов?",
      answers: ["Бешбармак", "Лагман", "Манты", "Жашылык"],
      correctAnswer: "Бешбармак",
    },
    {
      question: "Какой напиток является традиционным для кыргызов?",
      answers: ["Кумыс", "Айран", "Шубат", "Чай"],
      correctAnswer: "Кумыс",
    },
    {
      question: "Когда Кыргызстан объявил свою независимость?",
      answers: ["1991 год", "1995 год", "1989 год", "2000 год"],
      correctAnswer: "1991 год",
    },
  ];

  const handleAnswerClick = (answer) => {
    setSelectedAnswer(answer);
    const correct = answer === questions[currentQuestionIndex].correctAnswer;
    setIsCorrect(correct);
    setIsWrongAnswer(!correct);

    if (correct) {
      setShowConfetti(true);
      setTimeout(() => setShowConfetti(false), 3000);
    } else {
      setTimeout(() => setIsWrongAnswer(false), 1500);
    }
  };

  const nextQuestion = () => {
    if (currentQuestionIndex < questions.length - 1) {
      setCurrentQuestionIndex(currentQuestionIndex + 1);
      resetState();
    }
  };

  const prevQuestion = () => {
    if (currentQuestionIndex > 0) {
      setCurrentQuestionIndex(currentQuestionIndex - 1);
      resetState();
    }
  };

  const resetState = () => {
    setSelectedAnswer(null);
    setIsCorrect(null);
    setIsWrongAnswer(false);
  };

  const { question, answers } = questions[currentQuestionIndex];

  return (
    <Box
      sx={{
        position: "relative",
        minHeight: "100vh",
        overflow: "hidden",
        display: "flex",
        flexDirection: "column",
      }}
    >
      {/* Видео фон */}
      <Box
        sx={{
          position: "absolute",
          top: 0,
          left: 0,
          width: "100%",
          height: "100%",
          zIndex: -1,
        }}
      >
        <video
          className="background-video"
          src={videoBg}
          autoPlay
          loop
          muted
          playsInline
          style={{
            width: "100%",
            height: "100%",
            objectFit: "cover",
            filter: "none",
          }}
        />
        <div
          style={{
            position: "absolute",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            background:
              "linear-gradient(to bottom, rgba(0,0,0,0.5), rgba(0,0,0,0.9))",
          }}
        ></div>
      </Box>

      {/* Викторина */}
      <Box
        sx={{
          position: "relative",
          padding: "40px 20px",
          color: "white",
          textAlign: "center",
          borderRadius: "10px",
          overflow: "hidden",
          boxShadow: "0 4px 15px rgba(255, 255, 255, 0.1)",
        }}
      >
        <video
          className="quiz-background-video"
          src={natureKR}
          autoPlay
          loop
          muted
          playsInline
          preload="auto"
          style={{
            position: "absolute",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            objectFit: "cover",
            filter: "none",
            zIndex: -1,
          }}
        />

        <Card
          sx={{
            backgroundColor: "rgba(0, 0, 0, 0.6)",
            borderRadius: "10px",
            padding: "20px",
            width: "100%",
            maxWidth: "600px",
            margin: "0 auto",
            boxShadow: "0 4px 15px rgba(255, 255, 255, 0.2)",
            backdropFilter: "blur(10px)",
          }}
        >
          <Typography
            variant="h4"
            sx={{ color: "white", marginBottom: "20px" }}
          >
            {question}
          </Typography>

          <Box
            sx={{
              display: "flex",
              flexDirection: "column",
              alignItems: "center",
            }}
          >
            {answers.map((answer, index) => (
              <Card
                key={index}
                onClick={() => handleAnswerClick(answer)}
                sx={{
                  cursor: "pointer",
                  margin: "10px",
                  width: "100%",
                  padding: "10px",
                  textAlign: "center",
                  backgroundColor:
                    selectedAnswer === answer
                      ? isCorrect
                        ? "green"
                        : "red"
                      : "rgba(255, 255, 255, 0.1)",
                  transition: "background 0.3s ease",
                }}
              >
                <Typography variant="h6">{answer}</Typography>
              </Card>
            ))}
          </Box>

          <Box
            sx={{
              display: "flex",
              justifyContent: "space-between",
              marginTop: "20px",
            }}
          >
            <Button
              onClick={prevQuestion}
              disabled={currentQuestionIndex === 0}
              sx={{
                color: "white",
                border: "1px solid white",
                padding: "8px 16px",
              }}
            >
              Назад
            </Button>
            <Button
              onClick={nextQuestion}
              disabled={currentQuestionIndex === questions.length - 1}
              sx={{
                color: "white",
                border: "1px solid white",
                padding: "8px 16px",
              }}
            >
              Далее
            </Button>
          </Box>
        </Card>
      </Box>

      {showConfetti && <Confetti />}
    </Box>
  );
};

export default MainPage;

// Courier New, monospace

