import React, { useState } from "react";
import { Box, Button, Typography, TextField } from "@mui/material";
import videoBg from "../homePage/assets/videoBg.mp4"; // Путь к видео

const MainPage = () => {
  const [isVisible, setIsVisible] = useState(false);
  const [answer, setAnswer] = useState("");
  const [showResult, setShowResult] = useState(false);
  const [isCorrect, setIsCorrect] = useState(null);

  const handleQuizSubmit = () => {
    if (answer.toLowerCase() === "дмитрий баландин") {
      setIsCorrect(true);
    } else {
      setIsCorrect(false);
    }
    setShowResult(true);
  };

  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 200) {
        setIsVisible(true);
      } else {
        setIsVisible(false);
      }
    };
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <Box
      className="home-main-page"
      sx={{
        position: "relative",
        minHeight: "100vh",
        overflow: "hidden",
        display: "flex",
        flexDirection: "column",
      }}
    >
      {/* Видео фон */}
      <Box
        sx={{
          position: "absolute",
          top: 0,
          left: 0,
          width: "100%",
          height: "100%",
          zIndex: -1,
        }}
      >
        <video
          className="background-video"
          src={videoBg}
          autoPlay
          loop
          muted
          playsInline
          style={{
            width: "100%",
            height: "100%",
            objectFit: "cover",
            filter: "brightness(70%)", // Сделал фон темнее для лучшего контраста
          }}
        />
        <div
          style={{
            position: "absolute",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            background:
              "linear-gradient(to bottom, rgba(0,0,0,0.5), rgba(0,0,0,0.9))", // Более темный оверлей
          }}
        ></div>
      </Box>

      {/* Контент с анимацией */}
      <Box
        sx={{
          position: "relative",
          width: "100%",
          minHeight: "100vh",
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          textAlign: "center",
          color: "white",
          padding: "20px",
          opacity: isVisible ? 1 : 0,
          transform: isVisible ? "translateY(0)" : "translateY(30px)", // Меньше движения
          transition: "all 0.8s ease-out",
        }}
      >
        <Box sx={{ maxWidth: "800px" }}>
          <Typography
            variant="h1"
            sx={{
              fontSize: "48px",
              marginBottom: "15px",
              fontWeight: "bold",
              fontFamily: "Georgia, serif",
              letterSpacing: "1px",
            }}
          >
            Спортсмены Кыргызстана
          </Typography>
          <Typography
            variant="body1"
            sx={{
              fontSize: "clamp(16px, 2vw, 22px)",
              fontWeight: "500",
              lineHeight: "1.6",
              maxWidth: "90%",
              margin: "0 auto",
            }}
          >
            Спортсмены Кыргызстана завоевывают признание на международной арене
            в таких видах спорта, как борьба, дзюдо, бокс и легкая атлетика. Эти
            атлеты становятся символами силы, упорства и решимости. Их
            достижения вдохновляют новое поколение, показывая, что стремление к
            победе и трудолюбие открывают путь к вершинам.
          </Typography>
        </Box>
      </Box>

      {/* Блок с изображением с эффектом параллакса */}
      <Box
        sx={{
          textAlign: "center",
          padding: "20px",
          backgroundColor: "#121212",
          display: "flex",
          justifyContent: "center",
          position: "relative",
        }}
      >
        <img
          src="https://data.kaktus.media/image/big/2024-08-11_19-52-39_202865.jpg"
          alt="Кыргызстан"
          style={{
            width: "90%",
            maxWidth: "1400px",
            borderRadius: "12px",
            boxShadow: "0 4px 15px rgba(255, 255, 255, 0.1)",
            transition: "transform 0.3s ease, filter 0.3s ease",
            transform: "scale(1)",
            filter: "brightness(80%)",
          }}
          onMouseOver={(e) => {
            e.target.style.transform = "scale(1.03)";
            e.target.style.filter = "brightness(100%)";
          }}
          onMouseOut={(e) => {
            e.target.style.transform = "scale(1)";
            e.target.style.filter = "brightness(80%)";
          }}
        />
      </Box>

      {/* Викторина */}
      <Box
        sx={{
          textAlign: "center",
          marginTop: "40px",
          backgroundColor: "#333",
          borderRadius: "10px",
          padding: "30px",
          boxShadow: "0 6px 20px rgba(0, 0, 0, 0.4)",
        }}
      >
        <Typography
          variant="h3"
          sx={{
            color: "#fff",
            marginBottom: "20px",
            fontWeight: "bold",
            fontFamily: "Arial, sans-serif",
          }}
        >
          Викторина: Олимпийские чемпионы Кыргызстана
        </Typography>
        <Typography
          variant="body1"
          sx={{
            color: "#fff",
            marginBottom: "30px",
            fontSize: "18px",
            fontWeight: "400",
          }}
        >
          Кто является олимпийским чемпионом Кыргызстана в плавании?
        </Typography>
        <TextField
          value={answer}
          onChange={(e) => setAnswer(e.target.value)}
          variant="outlined"
          placeholder="Введите имя..."
          sx={{
            marginBottom: "20px",
            width: "80%",
            maxWidth: "400px",
            backgroundColor: "#fff",
            borderRadius: "8px",
          }}
        />
        <Button
          onClick={handleQuizSubmit}
          variant="contained"
          sx={{
            backgroundColor: "#ff4d6d",
            "&:hover": {
              backgroundColor: "#e03d56",
            },
            padding: "10px 20px",
            borderRadius: "50px",
            fontWeight: "bold",
          }}
        >
          Отправить
        </Button>
        {showResult && (
          <Box
            sx={{
              marginTop: "20px",
              color: isCorrect ? "green" : "red",
              fontSize: "20px",
              fontWeight: "500",
            }}
          >
            {isCorrect
              ? "Правильный ответ! Дмитрий Баландин!"
              : "Неправильный ответ. Попробуйте снова!"}
          </Box>
        )}
      </Box>

      {/* Кнопка с эффектом hover */}
      <Box
        sx={{
          display: "flex",
          justifyContent: "center",
          marginTop: "30px",
        }}
      ></Box>
    </Box>
  );
};

export default MainPage;

