import React, { useEffect, useState } from "react";
import { Button, Box, Typography } from "@mui/material";
import videoBg from "../homePage/assets/videoBg.mp4"; // Путь к видео
import Confetti from "react-confetti";

const MainPage = () => {
  const [isVisible, setIsVisible] = useState(false);
  const [questionIndex, setQuestionIndex] = useState(0);
  const [selectedOption, setSelectedOption] = useState(null);
  const [isCorrect, setIsCorrect] = useState(null);
  const [score, setScore] = useState({ correct: 0, incorrect: 0 });
  const [isQuizFinished, setIsQuizFinished] = useState(false);
  const [showConfetti, setShowConfetti] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 200) {
        setIsVisible(true);
      } else {
        setIsVisible(false);
      }
    };
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  const questions = [
    {
      question: "Какой спорт является национальным в Кыргызстане?",
      options: ["Футбол", "Борьба", "Кок-бору", "Баскетбол"],
      answer: "Кок-бору",
    },
    {
      question: "Сколько медалей завоевал Кыргызстан на Олимпийских играх?",
      options: ["3", "5", "7", "10"],
      answer: "7",
    },
  ];

  const handleAnswerSelect = (option) => {
    setSelectedOption(option);
    if (option === questions[questionIndex].answer) {
      setIsCorrect(true);
      setScore((prev) => ({ ...prev, correct: prev.correct + 1 }));
      setShowConfetti(true);
      setTimeout(() => setShowConfetti(false), 2000);
    } else {
      setIsCorrect(false);
      setScore((prev) => ({ ...prev, incorrect: prev.incorrect + 1 }));
    }
    setTimeout(() => {
      if (questionIndex < questions.length - 1) {
        setQuestionIndex(questionIndex + 1);
        setSelectedOption(null);
        setIsCorrect(null);
      } else {
        setIsQuizFinished(true);
      }
    }, 1500);
  };

  return (
    <Box
      className="home-main-page"
      sx={{
        position: "relative",
        minHeight: "100vh",
        overflow: "hidden",
        display: "flex",
        flexDirection: "column",
      }}
    >
      <Box
        sx={{
          position: "absolute",
          top: 0,
          left: 0,
          width: "100%",
          height: "100%",
          zIndex: -1,
        }}
      >
        <video
          className="background-video"
          src={videoBg}
          autoPlay
          loop
          muted
          playsInline
          style={{
            width: "100%",
            height: "100%",
            objectFit: "cover",
            filter: "brightness(70%)",
          }}
        />
        <div
          style={{
            position: "absolute",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            background:
              "linear-gradient(to bottom, rgba(0,0,0,0.5), rgba(0,0,0,0.9))",
          }}
        ></div>
      </Box>
      <Box
        sx={{
          position: "relative",
          width: "100%",
          minHeight: "100vh",
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          textAlign: "center",
          color: "white",
          padding: "20px",
          opacity: isVisible ? 1 : 0,
          transform: isVisible ? "translateY(0)" : "translateY(30px)",
          transition: "all 0.8s ease-out",
        }}
      >
        <Box sx={{ maxWidth: "800px" }}>
          <Typography
            variant="h1"
            sx={{
              fontSize: "48px",
              marginBottom: "15px",
              fontWeight: "bold",
              fontFamily: "Georgia, serif",
              letterSpacing: "1px",
            }}
          >
            Спортсмены Кыргызстана
          </Typography>
          <Typography
            variant="body1"
            sx={{
              fontSize: "clamp(16px, 2vw, 22px)",
              fontWeight: "500",
              lineHeight: "1.6",
              maxWidth: "90%",
              margin: "0 auto",
            }}
          >
            Спортсмены Кыргызстана завоевывают признание на международной арене
            в таких видах спорта, как борьба, дзюдо, бокс и легкая атлетика. Их
            достижения вдохновляют новое поколение.
          </Typography>
        </Box>
      </Box>

      {!isQuizFinished ? (
        <Box
          className="quiz-container"
          sx={{ textAlign: "center", marginTop: "40px", padding: "20px" }}
        >
          <Typography variant="h4">
            {questions[questionIndex].question}
          </Typography>
          <Box
            className="options"
            sx={{ display: "flex", flexDirection: "column", gap: "10px" }}
          >
            {questions[questionIndex].options.map((option, index) => (
              <Button
                key={index}
                variant="contained"
                color={
                  selectedOption === option
                    ? isCorrect
                      ? "success"
                      : "error"
                    : "primary"
                }
                onClick={() => handleAnswerSelect(option)}
              >
                {option}
              </Button>
            ))}
          </Box>
          {showConfetti && <Confetti />}
        </Box>
      ) : (
        <Box
          className="quiz-results"
          sx={{ textAlign: "center", marginTop: "40px" }}
        >
          <Typography variant="h4">Викторина завершена!</Typography>
          <Typography>Правильных ответов: {score.correct}</Typography>
          <Typography>Неправильных ответов: {score.incorrect}</Typography>
          <Button
            variant="contained"
            color="secondary"
            onClick={() => {
              setIsQuizFinished(false);
              setQuestionIndex(0);
              setScore({ correct: 0, incorrect: 0 });
            }}
          >
            Начать заново
          </Button>
        </Box>
      )}

      <Box
        sx={{
          textAlign: "center",
          padding: "20px",
          backgroundColor: "#121212",
          display: "flex",
          justifyContent: "center",
          position: "relative",
        }}
      >
        <img
          src="https://data.kaktus.media/image/big/2024-08-11_19-52-39_202865.jpg"
          alt="Кыргызстан"
          style={{
            width: "90%",
            maxWidth: "1400px",
            borderRadius: "12px",
            boxShadow: "0 4px 15px rgba(255, 255, 255, 0.1)",
            transition: "transform 0.3s ease, filter 0.3s ease",
          }}
        />
      </Box>
    </Box>
  );
};

export default MainPage;

// Courier New, monospace

