import React, { useEffect, useState } from "react";
import {
  Button,
  Box,
  Typography,
  Card,
  CardContent,
  CardActions,
} from "@mui/material";
import videoBg from "../homePage/assets/videoBg.mp4"; // Путь к видео
import Flag_of_the_KR from "../homePage/assets/Flag_of_the_KR.mp4"; // Путь к видео

const MainPage = () => {
  const [isVisible, setIsVisible] = useState(false);
  const [showQuiz, setShowQuiz] = useState(false);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [selectedOption, setSelectedOption] = useState(null);
  const [quizResults, setQuizResults] = useState(0);
  const [quizFeedback, setQuizFeedback] = useState("");

  const questions = [
    {
      question: "Кто выиграл первую олимпийскую медаль для Кыргызстана?",
      options: [
        "Айдос Султангали",
        "Канатбек Бегалиев",
        "Александр Беляев",
        "Айсулуу Тыныбекова",
      ],
      correctAnswer: 1,
    },
    {
      question: "В каком виде спорта Кыргызстан завоевал больше всего медалей?",
      options: ["Легкая атлетика", "Борьба", "Бокс", "Дзюдо"],
      correctAnswer: 1,
    },
  ];

  useEffect(() => {
    const handleScroll = () => {
      setIsVisible(window.scrollY > 200);
    };
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  const handleOptionClick = (idx) => {
    setSelectedOption(idx);
    if (idx === questions[currentQuestion].correctAnswer) {
      setQuizResults((prev) => prev + 1);
      setQuizFeedback("Правильный ответ!");
    } else {
      setQuizFeedback("Неправильный ответ!");
    }
  };

  const handleNextQuestion = () => {
    if (currentQuestion < questions.length - 1) {
      setCurrentQuestion((prev) => prev + 1);
      setSelectedOption(null);
      setQuizFeedback("");
    } else {
      setShowQuiz(true);
    }
  };

  return (
    <Box
      className="home-main-page"
      sx={{
        position: "relative",
        minHeight: "100vh",
        overflow: "hidden",
        display: "flex",
        flexDirection: "column",
      }}
    >
      {/* Видео фон */}
      <Box
        sx={{
          position: "absolute",
          top: 0,
          left: 0,
          width: "100%",
          height: "100%",
          zIndex: -1,
        }}
      >
        <video
          className="background-video"
          src={videoBg}
          autoPlay
          loop
          muted
          playsInline
          style={{
            width: "100%",
            height: "100%",
            objectFit: "cover",
            filter: "brightness(80%)",
          }}
        />
        <div
          style={{
            position: "absolute",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            background:
              "linear-gradient(to bottom, rgba(0,0,0,0.5), rgba(0,0,0,0.9))",
          }}
        ></div>
      </Box>

      {/* Контент */}
      <Box
        sx={{
          position: "relative",
          width: "100%",
          minHeight: "100vh",
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          textAlign: "center",
          color: "white",
          padding: "20px",
          opacity: isVisible ? 1 : 0,
          transform: isVisible ? "translateY(0)" : "translateY(30px)",
          transition: "all 0.8s ease-out",
        }}
      >
        <Box sx={{ maxWidth: "800px" }}>
          <Typography
            variant="h1"
            sx={{
              fontSize: "48px",
              marginBottom: "15px",
              fontWeight: "bold",
              fontFamily: "Georgia, serif",
              letterSpacing: "1px",
            }}
          >
            Спортсмены Кыргызстана
          </Typography>
          <Typography
            variant="body1"
            sx={{
              fontSize: "clamp(16px, 2vw, 22px)",
              fontWeight: "500",
              lineHeight: "1.6",
              maxWidth: "90%",
              margin: "0 auto",
            }}
          >
            Спортсмены Кыргызстана завоевывают признание на международной арене
            в таких видах спорта, как борьба, дзюдо, бокс и легкая атлетика.
          </Typography>
        </Box>
      </Box>

      {/* Викторина */}
      <Box
        sx={{
          display: "flex",
          justifyContent: "center",
          marginTop: "30px",
          position: "relative",
          padding: "40px 20px",
          color: "white",
          textAlign: "center",
          borderRadius: "10px",
          overflow: "hidden",
          boxShadow: "0 4px 15px rgba(255, 255, 255, 0.1)",
        }}
      >
        <video
          src={Flag_of_the_KR}
          autoPlay
          loop
          muted
          playsInline
          style={{
            position: "absolute",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            objectFit: "cover",
            zIndex: -1,
            filter: "brightness(60%)",
          }}
        />

        <Box
          sx={{
            position: "absolute",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            background: "rgba(0, 0, 0, 0.5)",
            zIndex: -1,
          }}
        ></Box>

        <Box>
          <Typography
            variant="h2"
            sx={{
              marginBottom: "20px",
              fontWeight: "bold",
              color: "#ffcc00",
              fontSize: "28px",
              letterSpacing: "2px",
              textTransform: "uppercase",
            }}
          >
            Викторина: Олимпийские чемпионы Кыргызстана
          </Typography>

          {showQuiz ? (
            <Typography
              sx={{
                marginTop: "20px",
                fontSize: "24px",
                fontWeight: "bold",
                color: "green",
              }}
            >
              Результат: {quizResults} из {questions.length} правильных ответов
            </Typography>
          ) : (
            <Card sx={{ maxWidth: 400, margin: "20px auto", padding: "20px" }}>
              <CardContent>
                <Typography variant="h5">
                  {questions[currentQuestion].question}
                </Typography>
                {questions[currentQuestion].options.map((option, idx) => (
                  <Button
                    key={idx}
                    variant="contained"
                    color="primary"
                    onClick={() => handleOptionClick(idx)}
                  >
                    {option}
                  </Button>
                ))}
                <Typography
                  sx={{
                    marginTop: "10px",
                    fontWeight: "bold",
                    color:
                      quizFeedback === "Правильный ответ!" ? "green" : "red",
                  }}
                >
                  {quizFeedback}
                </Typography>
              </CardContent>
              <CardActions>
                <Button
                  variant="contained"
                  color="secondary"
                  onClick={handleNextQuestion}
                >
                  {currentQuestion === questions.length - 1
                    ? "Завершить"
                    : "Следующий вопрос"}
                </Button>
              </CardActions>
            </Card>
          )}
        </Box>
      </Box>
    </Box>
  );
};

export default MainPage;

// Courier New, monospace

