import React, { useEffect, useState } from "react";
import { Button, Box, Typography, Card, CardContent } from "@mui/material";
import Confetti from "react-confetti";
import videoBg from "../homePage/assets/videoBg.mp4";
import natureKR from "../homePage/assets/natureKR.mp4";

const MainPage = () => {
  const [isVisible, setIsVisible] = useState(false);
  const [selectedAnswer, setSelectedAnswer] = useState(null);
  const [isCorrect, setIsCorrect] = useState(null);
  const [showConfetti, setShowConfetti] = useState(false);
  const [isWrongAnswer, setIsWrongAnswer] = useState(false);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [score, setScore] = useState(0);
  const [quizFinished, setQuizFinished] = useState(false); // Flag to indicate quiz completion

  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 200) {
        setIsVisible(true);
      } else {
        setIsVisible(false);
      }
    };
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  const questions = [
    {
      question: "Какой самый высокий пик в Кыргызстане?",
      answers: ["Пик Ленина", "Пик Победы", "Пик Манас", "Пик Корона"],
      correctAnswer: "Пик Победы",
    },
    {
      question: "Какое национальное блюдо кыргызов?",
      answers: ["Бешбармак", "Лагман", "Манты", "Жашылык"],
      correctAnswer: "Бешбармак",
    },
    {
      question: "Какой напиток является традиционным для кыргызов?",
      answers: ["Кумыс", "Айран", "Шубат", "Чай"],
      correctAnswer: "Кумыс",
    },
    {
      question: "Когда Кыргызстан объявил свою независимость?",
      answers: ["1991 год", "1995 год", "1989 год", "2000 год"],
      correctAnswer: "1991 год",
    },
    {
      question: "Какая столица Кыргызстана?",
      answers: ["Бишкек", "Ош", "Нарын", "Талас"],
      correctAnswer: "Бишкек",
    },
    {
      question: "Какой язык является государственным в Кыргызстане?",
      answers: ["Русский", "Казахский", "Кыргызский", "Таджикский"],
      correctAnswer: "Кыргызский",
    },
    {
      question: "Какая валюта используется в Кыргызстане?",
      answers: ["Тенге", "Рубль", "Сом", "Доллар"],
      correctAnswer: "Сом",
    },
    {
      question:
        "Как называется национальный праздник Кыргызстана, посвящённый независимости?",
      answers: ["Нооруз", "День независимости", "Манас", "Ош"],
      correctAnswer: "День независимости",
    },
    {
      question:
        "Какая горная система занимает большую часть территории Кыргызстана?",
      answers: ["Горы Тянь-Шаня", "Кавказ", "Урал", "Альпы"],
      correctAnswer: "Горы Тянь-Шаня",
    },
    {
      question: "Какое животное является символом Кыргызстана?",
      answers: ["Овца", "Лошадь", "Козёл", "Як"],
      correctAnswer: "Лошадь",
    },
  ];

  const handleAnswerClick = (answer) => {
    setSelectedAnswer(answer);
    const correct = answer === questions[currentQuestionIndex].correctAnswer;
    setIsCorrect(correct);
    setIsWrongAnswer(!correct);

    if (correct) {
      setScore(score + 1);
      setShowConfetti(true);
      setTimeout(() => setShowConfetti(false), 5000);
    } else {
      setTimeout(() => setIsWrongAnswer(false), 1500);
    }

    // Переключаем на следующий вопрос
    setTimeout(() => {
      if (currentQuestionIndex < questions.length - 1) {
        setCurrentQuestionIndex(currentQuestionIndex + 1);
        setSelectedAnswer(null);
        setIsCorrect(null);
      } else {
        setQuizFinished(true); // Завершаем викторину
      }
    }, 2000); // Задержка для переключения вопросов
  };

  const handleRestartQuiz = () => {
    setScore(0);
    setCurrentQuestionIndex(0);
    setQuizFinished(false);
    setSelectedAnswer(null);
    setIsCorrect(null);
    setIsWrongAnswer(false);
  };

  const { question, answers } = questions[currentQuestionIndex];

  return (
    <Box
      sx={{
        position: "relative",
        minHeight: "100vh",
        overflow: "hidden",
        display: "flex",
        flexDirection: "column",
      }}
    >
      {/* Видео фон */}
      <Box
        sx={{
          position: "absolute",
          top: 0,
          left: 0,
          width: "100%",
          height: "100%",
          zIndex: -1,
        }}
      >
        <video
          className="background-video"
          src={videoBg}
          autoPlay
          loop
          muted
          playsInline
          style={{
            width: "100%",
            height: "100%",
            objectFit: "cover",
            filter: "none",
          }}
        />
        <div
          style={{
            position: "absolute",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            background:
              "linear-gradient(to bottom, rgba(0,0,0,0.5), rgba(0,0,0,0.9))",
          }}
        ></div>
      </Box>

      {/* Викторина */}
      <Box
        sx={{
          position: "relative",
          padding: "40px 20px",
          color: "white",
          textAlign: "center",
          borderRadius: "10px",
          overflow: "hidden",
          boxShadow: "0 4px 15px rgba(255, 255, 255, 0.1)",
        }}
      >
        <video
          className="quiz-background-video"
          src={natureKR}
          autoPlay
          loop
          muted
          playsInline
          preload="auto"
          style={{
            position: "absolute",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            objectFit: "cover",
            filter: "none",
            zIndex: -1,
          }}
        />

        <Card
          sx={{
            backgroundColor: "rgba(0, 0, 0, 0.6)",
            borderRadius: "10px",
            padding: "20px",
            width: "100%",
            maxWidth: "600px",
            margin: "0 auto",
            boxShadow: "0 4px 15px rgba(255, 255, 255, 0.2)",
            backdropFilter: "blur(10px)",
          }}
        >
          <Typography
            variant="h4"
            sx={{ color: "white", marginBottom: "20px" }}
          >
            {question}
          </Typography>
          <Box
            sx={{
              display: "flex",
              flexDirection: "column",
              alignItems: "center",
            }}
          >
            {answers.map((answer, index) => (
              <Card
                key={index}
                onClick={() => handleAnswerClick(answer)}
                sx={{
                  cursor: "pointer",
                  margin: "10px",
                  width: "100%",
                  backgroundColor:
                    selectedAnswer === answer
                      ? isCorrect
                        ? "rgba(0, 150, 0, 0.7)"
                        : "rgba(150, 0, 0, 0.7)"
                      : "rgba(255, 255, 255, 0.1)",
                  color: "white",
                  border: "1px solid rgba(255, 255, 255, 0.3)",
                  "&:hover": {
                    transform: "scale(1.05)",
                    backgroundColor: "rgba(255, 255, 255, 0.3)",
                    filter: "brightness(1.2)",
                  },
                  animation: isWrongAnswer ? "shake 0.6s ease-out" : "none",
                }}
              >
                <CardContent>
                  <Typography>{answer}</Typography>
                </CardContent>
              </Card>
            ))}
            {selectedAnswer && (
              <Typography
                sx={{
                  marginTop: "20px",
                  color: isCorrect ? "lightgreen" : "red",
                }}
              >
                {isCorrect ? "Верно!" : "Неверно! Попробуйте ещё раз."}
              </Typography>
            )}
          </Box>
        </Card>
        {showConfetti && <Confetti />}
        {quizFinished && (
          <Box sx={{ marginTop: "20px" }}>
            <Typography variant="h5" sx={{ color: "white" }}>
              Ваш результат: {score} из {questions.length}
            </Typography>
            <Button
              onClick={handleRestartQuiz}
              sx={{
                marginTop: "20px",
                color: "#fff",
                backgroundColor: "#4CAF50",
                "&:hover": { backgroundColor: "#45a049" },
              }}
            >
              Пройти викторину заново
            </Button>
          </Box>
        )}
      </Box>

      {/* Добавляем CSS для анимации */}
      <style>
        {`
          @keyframes shake {
            0% { transform: translateX(0); }
            25% { transform: translateX(-10px); }
            50% { transform: translateX(10px); }
            75% { transform: translateX(-10px); }
            100% { transform: translateX(0); }
          }
        `}
      </style>
    </Box>
  );
};

export default MainPage;

