import React, { useEffect, useState } from "react";
import {
  Button,
  Box,
  Typography,
  Radio,
  RadioGroup,
  FormControl,
  FormControlLabel,
  FormLabel,
} from "@mui/material";
import videoBg from "../homePage/assets/videoBg.mp4"; // Путь к видео

const MainPage = () => {
  const [isVisible, setIsVisible] = useState(false);
  const [selectedAnswer, setSelectedAnswer] = useState("");
  const [questionIndex, setQuestionIndex] = useState(0);

  const questions = [
    {
      question:
        "Кто является самым успешным олимпийским спортсменом Кыргызстана?",
      options: [
        "Рустам Таштанов",
        "Айсулуу Тыныбекова",
        "Эльмира Сыздыкова",
        "Нурбол Талантбеков",
      ],
      answer: "Айсулуу Тыныбекова",
    },
    {
      question:
        "В каком виде спорта Кыргызстан завоевал первую олимпийскую медаль?",
      options: ["Бокс", "Дзюдо", "Борьба", "Легкая атлетика"],
      answer: "Борьба",
    },
    {
      question: "Когда был основан Олимпийский комитет Кыргызстана?",
      options: ["1992", "2000", "1988", "1996"],
      answer: "1992",
    },
  ];

  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 200) {
        setIsVisible(true);
      } else {
        setIsVisible(false);
      }
    };
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  const handleAnswerChange = (event) => {
    setSelectedAnswer(event.target.value);
  };

  const handleNextQuestion = () => {
    if (selectedAnswer === questions[questionIndex].answer) {
      alert("Правильный ответ!");
    } else {
      alert("Неправильный ответ.");
    }
    setSelectedAnswer("");
    if (questionIndex < questions.length - 1) {
      setQuestionIndex(questionIndex + 1);
    } else {
      alert("Поздравляем! Вы завершили викторину!");
    }
  };

  return (
    <Box
      className="home-main-page"
      sx={{
        position: "relative",
        minHeight: "100vh",
        overflow: "hidden",
        display: "flex",
        flexDirection: "column",
      }}
    >
      {/* Видео фон */}
      <Box
        sx={{
          position: "absolute",
          top: 0,
          left: 0,
          width: "100%",
          height: "100%",
          zIndex: -1,
        }}
      >
        <video
          className="background-video"
          src={videoBg}
          autoPlay
          loop
          muted
          playsInline
          style={{
            width: "100%",
            height: "100%",
            objectFit: "cover",
            filter: "brightness(70%)", // Сделал фон темнее для лучшего контраста
          }}
        />
        <div
          style={{
            position: "absolute",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            background:
              "linear-gradient(to bottom, rgba(0,0,0,0.5), rgba(0,0,0,0.9))", // Более темный оверлей
          }}
        ></div>
      </Box>

      {/* Контент с анимацией */}
      <Box
        sx={{
          position: "relative",
          width: "100%",
          minHeight: "100vh",
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          textAlign: "center",
          color: "white",
          padding: "20px",
          opacity: isVisible ? 1 : 0,
          transform: isVisible ? "translateY(0)" : "translateY(30px)", // Меньше движения
          transition: "all 0.8s ease-out",
        }}
      >
        <Box sx={{ maxWidth: "800px" }}>
          <Typography
            variant="h1"
            sx={{
              fontSize: "48px",
              marginBottom: "15px",
              fontWeight: "bold",
              fontFamily: "Georgia, serif",
              letterSpacing: "1px",
            }}
          >
            Спортсмены Кыргызстана
          </Typography>
          <Typography
            variant="body1"
            sx={{
              fontSize: "clamp(16px, 2vw, 22px)",
              fontWeight: "500",
              lineHeight: "1.6",
              maxWidth: "90%",
              margin: "0 auto",
            }}
          >
            Спортсмены Кыргызстана завоевывают признание на международной арене
            в таких видах спорта, как борьба, дзюдо, бокс и легкая атлетика. Эти
            атлеты становятся символами силы, упорства и решимости. Их
            достижения вдохновляют новое поколение, показывая, что стремление к
            победе и трудолюбие открывают путь к вершинам.
          </Typography>
        </Box>
      </Box>

      {/* Блок с изображением с эффектом параллакса */}
      <Box
        sx={{
          textAlign: "center",
          padding: "20px",
          backgroundColor: "#121212",
          display: "flex",
          justifyContent: "center",
          position: "relative",
        }}
      >
        <img
          src="https://data.kaktus.media/image/big/2024-08-11_19-52-39_202865.jpg"
          alt="Кыргызстан"
          style={{
            width: "90%",
            maxWidth: "1400px",
            borderRadius: "12px",
            boxShadow: "0 4px 15px rgba(255, 255, 255, 0.1)",
            transition: "transform 0.3s ease, filter 0.3s ease",
            transform: "scale(1)",
            filter: "brightness(80%)",
          }}
          onMouseOver={(e) => {
            e.target.style.transform = "scale(1.03)";
            e.target.style.filter = "brightness(100%)";
          }}
          onMouseOut={(e) => {
            e.target.style.transform = "scale(1)";
            e.target.style.filter = "brightness(80%)";
          }}
        />
      </Box>

      {/* Викторина */}
      <Box
        sx={{
          padding: "40px 20px",
          backgroundColor: "#1c1c1c",
          marginTop: "40px",
          borderRadius: "8px",
        }}
      >
        <Typography variant="h4" sx={{ color: "white", marginBottom: "20px" }}>
          Викторина о спорте Кыргызстана
        </Typography>
        <Typography variant="h6" sx={{ color: "white", marginBottom: "15px" }}>
          {questions[questionIndex].question}
        </Typography>
        <FormControl component="fieldset" sx={{ marginBottom: "20px" }}>
          <RadioGroup value={selectedAnswer} onChange={handleAnswerChange}>
            {questions[questionIndex].options.map((option, index) => (
              <FormControlLabel
                key={index}
                value={option}
                control={
                  <Radio
                    sx={{
                      color: "#fff",
                      "&.Mui-checked": { color: "#2196f3" },
                    }}
                  />
                }
                label={option}
                sx={{ color: "white", fontSize: "18px" }}
              />
            ))}
          </RadioGroup>
        </FormControl>
        <Button
          variant="contained"
          sx={{
            backgroundColor: "#2196f3",
            padding: "10px 20px",
            fontSize: "18px",
            "&:hover": {
              backgroundColor: "#1976d2",
            },
          }}
          onClick={handleNextQuestion}
        >
          Ответить
        </Button>
      </Box>
    </Box>
  );
};

export default MainPage;

