import React, { useEffect, useState } from "react";
import {
  AppBar,
  Toolbar,
  Typography,
  IconButton,
  Drawer,
  List,
  ListItem,
  ListItemText,
} from "@mui/material";
import { Link } from "react-router-dom";
import { useAuth } from "../../components/context/AuthContextProvider";
import { ADMIN_EMAIL } from "../../helpers/const";
import MenuIcon from "@mui/icons-material/Menu";

const Navbar = () => {
  const { user, handleLogOut, loading } = useAuth();
  const [isAdmin, setIsAdmin] = useState(false);
  const [drawerOpen, setDrawerOpen] = useState(false); // Для управления открытием меню

  useEffect(() => {
    if (user) {
      setIsAdmin(user.email === ADMIN_EMAIL);
    }
  }, [user]);

  const pages = [
    { id: 1, title: "Атлеты", link: "/athletes" },
    { id: 2, title: "Виды спорта", link: "/sports" },
    { id: 3, title: "Медальный зачет", link: "/medal-table" },
  ];

  if (isAdmin) {
    pages.push({ id: 4, title: "Админ", link: "/admin" });
  }

  const toggleDrawer = (open) => {
    setDrawerOpen(open);
  };

  return (
    <div style={styles.root}>
      <AppBar position="static" style={styles.appBar}>
        <Toolbar style={styles.toolbar}>
          <div style={styles.leftItems}>
            <img
              src="https://upload.wikimedia.org/wikipedia/commons/thumb/4/41/Flag_of_Kyrgyzstan_%283-2%29.svg/1599px-Flag_of_Kyrgyzstan_%283-2%29.svg.png?20240311163127"
              style={{ ...styles.icon, width: "auto", height: 40 }}
              alt="Flag"
            />
            <Link to={"/"}>
              <Typography
                variant="h6"
                style={{ textDecoration: "none", color: "#ffffff" }}
              >
                Спортсмены КР
              </Typography>
            </Link>
          </div>
          <div style={{ ...styles.centerItems, flex: 1 }}>
            {pages.map((page, index) => (
              <IconButton
                key={page.id}
                color="inherit"
                component={Link}
                to={page.link}
                style={{
                  ...styles.menuButton,
                  marginLeft: index > 0 ? 10 : 0,
                }}
              >
                <Typography variant="body1" style={styles.menuItem}>
                  {page.title}
                </Typography>
              </IconButton>
            ))}
          </div>
          <div style={{ ...styles.rightItems, marginLeft: "auto" }}>
            <IconButton
              edge="end"
              color="inherit"
              aria-label="menu"
              onClick={() => toggleDrawer(true)}
              style={styles.menuButton}
            >
              <MenuIcon />
            </IconButton>
          </div>
        </Toolbar>
      </AppBar>

      <Drawer
        anchor="right"
        open={drawerOpen}
        onClose={() => toggleDrawer(false)}
      >
        <List style={styles.drawerList}>
          {!loading && (
            <ListItem button>
              <Typography style={{ color: "#000000" }}>
                {user
                  ? isAdmin
                    ? `Добро пожаловать, ${user.email}`
                    : `Добро пожаловать, ${user.displayName || user.email}`
                  : "Добро пожаловать, гость"}
              </Typography>
            </ListItem>
          )}
          {!loading && !user && (
            <ListItem button component={Link} to="/register">
              <ListItemText primary="Регистрация" />
            </ListItem>
          )}
          {!loading && user && (
            <ListItem button onClick={handleLogOut}>
              <ListItemText primary="Выйти" />
            </ListItem>
          )}
        </List>
      </Drawer>
    </div>
  );
};

const styles = {
  root: {
    flexGrow: 1,
    overflow: "hidden",
    width: "100%",
    height: "auto",
  },
  appBar: {
    backgroundColor: "#1a1a1a",
    color: "#ffffff",
    overflow: "hidden",
  },
  toolbar: {
    display: "flex",
    justifyContent: "space-between",
    overflow: "hidden",
  },
  leftItems: {
    display: "flex",
    alignItems: "center",
    overflow: "hidden",
  },
  centerItems: {
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    overflow: "hidden",
  },
  rightItems: {
    display: "flex",
    alignItems: "center",
    overflow: "hidden",
  },
  menuButton: {
    marginRight: 10,
    overflow: "hidden",
  },
  menuItem: {
    color: "#ffffff",
  },
  icon: {
    width: "auto",
    height: 40,
    marginRight: 10,
    overflow: "hidden",
  },
  drawerList: {
    width: 250,
  },
};

export default Navbar;
