import React, { useState } from "react";
import { useAuth } from "../components/context/AuthContextProvider";
import "../pages/Auth.css";

const Auth = () => {
  // Получение функций и состояния авторизации
  const {
    email,
    setEmail,
    password,
    setPassword,
    handleRegister,
    handleLogin,
    emailError,
    passwordError,
  } = useAuth();

  // Локальное состояние для управления полями
  const [isRegister, setIsRegister] = useState(true);
  const [name, setName] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [confirmPasswordError, setConfirmPasswordError] = useState("");

  // Обработчик отправки формы
  const handleSubmit = (e) => {
    e.preventDefault(); // Предотвращаем стандартное поведение формы
    if (isRegister && password !== confirmPassword) {
      setConfirmPasswordError("Пароли не совпадают");
      return;
    }
    setConfirmPasswordError("");

    if (email && password) {
      if (isRegister) {
        handleRegister(); // Регистрация
      } else {
        handleLogin(); // Вход
      }
    }
  };

  return (
    <div className="auth-container">
      {/* Левый раздел */}
      <div className="left-section"></div>

      {/* Правый раздел с формой */}
      <div className="right-section">
        <div className="auth-form">
          <h2 className="auth-title">{isRegister ? "Регистрация" : "Вход"}</h2>
          <form onSubmit={handleSubmit}>
            {isRegister && (
              <div className="input-group">
                <input
                  type="text"
                  placeholder="Ваше имя"
                  required
                  value={name}
                  onChange={(e) => setName(e.target.value)}
                />
              </div>
            )}
            <div className="input-group">
              <input
                type="email"
                placeholder="Электронная почта"
                autoFocus
                required
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
              <p className="error-msg">{emailError}</p>
            </div>
            <div className="input-group">
              <input
                type="password"
                placeholder="Пароль"
                required
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
              <p className="error-msg">{passwordError}</p>
            </div>
            {isRegister && (
              <div className="input-group">
                <input
                  type="password"
                  placeholder="Подтвердите пароль"
                  required
                  value={confirmPassword}
                  onChange={(e) => setConfirmPassword(e.target.value)}
                />
                <p className="error-msg">{confirmPasswordError}</p>
              </div>
            )}
            <div className="btn-container">
              <button type="submit">
                {isRegister ? "Регистрация" : "Вход"}
              </button>
              <p>
                {isRegister ? "Уже есть аккаунт?" : "Нет аккаунта?"}{" "}
                <span onClick={() => setIsRegister(!isRegister)}>
                  {isRegister ? "Вход" : "Регистрация"}
                </span>
              </p>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Auth;
