import React, { useState } from "react";
import {
  Box,
  Typography,
  Button,
  Radio,
  RadioGroup,
  FormControlLabel,
  FormControl,
  FormLabel,
} from "@mui/material";

const MainPage = () => {
  const [selectedAnswer, setSelectedAnswer] = useState("");
  const [answerStatus, setAnswerStatus] = useState(""); // Состояние для отображения правильности ответа

  const handleAnswerChange = (event) => {
    const answer = event.target.value;
    setSelectedAnswer(answer);
    checkAnswer(answer); // Проверка ответа сразу после выбора
  };

  const checkAnswer = (answer) => {
    const correctAnswer = "A"; // Правильный ответ (например, вариант "A")
    if (answer === correctAnswer) {
      setAnswerStatus("Правильный ответ!");
    } else {
      setAnswerStatus("Неправильный ответ, попробуйте еще раз.");
    }
  };

  return (
    <Box
      className="home-main-page"
      sx={{
        position: "relative",
        minHeight: "100vh",
        overflow: "hidden",
        display: "flex",
        flexDirection: "column",
        padding: "20px",
      }}
    >
      {/* Викторина */}
      <Box sx={{ marginTop: "40px", textAlign: "center" }}>
        <Typography
          variant="h4"
          sx={{ fontWeight: "bold", marginBottom: "20px" }}
        >
          Викторина: Кто выиграл золото на последних Олимпийских играх?
        </Typography>
        <FormControl component="fieldset" sx={{ marginBottom: "20px" }}>
          <FormLabel component="legend">Выберите правильный вариант:</FormLabel>
          <RadioGroup
            aria-label="quiz"
            name="quiz"
            value={selectedAnswer}
            onChange={handleAnswerChange}
            sx={{ display: "flex", justifyContent: "center" }}
          >
            <FormControlLabel
              value="A"
              control={<Radio />}
              label="Вариант A: Спортсмен 1"
            />
            <FormControlLabel
              value="B"
              control={<Radio />}
              label="Вариант B: Спортсмен 2"
            />
            <FormControlLabel
              value="C"
              control={<Radio />}
              label="Вариант C: Спортсмен 3"
            />
            <FormControlLabel
              value="D"
              control={<Radio />}
              label="Вариант D: Спортсмен 4"
            />
          </RadioGroup>
        </FormControl>

        {/* Отображение результата */}
        {answerStatus && (
          <Typography
            variant="h6"
            sx={{
              color: answerStatus === "Правильный ответ!" ? "green" : "red",
              fontWeight: "bold",
            }}
          >
            {answerStatus}
          </Typography>
        )}

        {/* Дополнительная кнопка для очистки */}
        <Button
          variant="contained"
          sx={{
            marginTop: "20px",
            backgroundColor: "#ff4d6d",
            "&:hover": {
              backgroundColor: "#ff1f5f",
            },
          }}
          onClick={() => {
            setSelectedAnswer("");
            setAnswerStatus(""); // Очистка состояния
          }}
        >
          Попробовать снова
        </Button>
      </Box>
    </Box>
  );
};

export default MainPage;

