import React, { useEffect, useState } from "react";
import { Button, Box, Typography, TextField } from "@mui/material";
import videoBg from "../homePage/assets/videoBg.mp4"; // Путь к видео

const MainPage = () => {
  const [isVisible, setIsVisible] = useState(false);
  const [quizStep, setQuizStep] = useState(0);
  const [userAnswer, setUserAnswer] = useState("");
  const [quizResult, setQuizResult] = useState("");

  const quizQuestions = [
    {
      question:
        "Кто завоевал первое золото для Кыргызстана на Олимпийских играх?",
      options: [
        "Алишер Узаков",
        "Айсулуу Тыныбекова",
        "Равшан Аскеров",
        "Светлана Тищенко",
      ],
      answer: "Айсулуу Тыныбекова",
    },
    {
      question:
        "В каком году Кыргызстан впервые стал участником Олимпийских игр?",
      options: ["1992", "1996", "2000", "1988"],
      answer: "1992",
    },
    {
      question:
        "Какой вид спорта принес Кыргызстану больше всего медалей на Олимпийских играх?",
      options: ["Бокс", "Борьба", "Дзюдо", "Теннис"],
      answer: "Борьба",
    },
  ];

  const handleAnswer = (option) => {
    if (quizQuestions[quizStep].answer === option) {
      setQuizResult("Правильный ответ!");
    } else {
      setQuizResult("Неправильный ответ!");
    }

    setTimeout(() => {
      if (quizStep < quizQuestions.length - 1) {
        setQuizStep(quizStep + 1);
        setUserAnswer("");
        setQuizResult("");
      } else {
        setQuizStep(0);
        setQuizResult("Поздравляем, вы завершили викторину!");
      }
    }, 1500);
  };

  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 200) {
        setIsVisible(true);
      } else {
        setIsVisible(false);
      }
    };
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <Box
      className="home-main-page"
      sx={{
        position: "relative",
        minHeight: "100vh",
        overflow: "hidden",
        display: "flex",
        flexDirection: "column",
      }}
    >
      {/* Видео фон */}
      <Box
        sx={{
          position: "absolute",
          top: 0,
          left: 0,
          width: "100%",
          height: "100%",
          zIndex: -1,
        }}
      >
        <video
          className="background-video"
          src={videoBg}
          autoPlay
          loop
          muted
          playsInline
          style={{
            width: "100%",
            height: "100%",
            objectFit: "cover",
            filter: "brightness(80%)", // Сделал фон темнее для лучшего контраста
          }}
        />
        <div
          style={{
            position: "absolute",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            background:
              "linear-gradient(to bottom, rgba(0,0,0,0.5), rgba(0,0,0,0.9))", // Более темный оверлей
          }}
        ></div>
      </Box>

      {/* Контент с анимацией */}
      <Box
        sx={{
          position: "relative",
          width: "100%",
          minHeight: "100vh",
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          textAlign: "center",
          color: "white",
          padding: "20px",
          opacity: isVisible ? 1 : 0,
          transform: isVisible ? "translateY(0)" : "translateY(30px)", // Меньше движения
          transition: "all 0.8s ease-out",
        }}
      >
        <Box sx={{ maxWidth: "800px" }}>
          <Typography
            variant="h1"
            sx={{
              fontSize: "48px",
              marginBottom: "15px",
              fontWeight: "bold",
              fontFamily: "Georgia, serif",
              letterSpacing: "1px",
            }}
          >
            Спортсмены Кыргызстана
          </Typography>
          <Typography
            variant="body1"
            sx={{
              fontSize: "clamp(16px, 2vw, 22px)",
              fontWeight: "500",
              lineHeight: "1.6",
              maxWidth: "90%",
              margin: "0 auto",
            }}
          >
            Спортсмены Кыргызстана завоевывают признание на международной арене
            в таких видах спорта, как борьба, дзюдо, бокс и легкая атлетика. Эти
            атлеты становятся символами силы, упорства и решимости. Их
            достижения вдохновляют новое поколение, показывая, что стремление к
            победе и трудолюбие открывают путь к вершинам.
          </Typography>
        </Box>
      </Box>

      {/* Викторина про Олимпийских чемпионов КР */}
      <Box
        sx={{
          backgroundColor: "#121212",
          padding: "40px",
          textAlign: "center",
          borderRadius: "12px",
          boxShadow: "0 4px 15px rgba(255, 255, 255, 0.1)",
          marginTop: "50px",
          transform: "scale(1)",
          filter: "brightness(90%)",
          transition: "transform 0.3s ease, filter 0.3s ease",
        }}
      >
        <Typography
          variant="h2"
          sx={{
            fontSize: "36px",
            marginBottom: "20px",
            fontWeight: "bold",
            color: "white",
            animation: "fadeIn 1s ease-out",
          }}
        >
          Викторина: Олимпийские чемпионы Кыргызстана
        </Typography>

        <Typography
          variant="h4"
          sx={{
            fontSize: "24px",
            marginBottom: "15px",
            fontWeight: "600",
            color: "#ffd700",
          }}
        >
          {quizQuestions[quizStep].question}
        </Typography>

        <Box
          sx={{
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
            gap: "10px",
            marginBottom: "30px",
          }}
        >
          {quizQuestions[quizStep].options.map((option, index) => (
            <Button
              key={index}
              onClick={() => handleAnswer(option)}
              sx={{
                backgroundColor: "#ff4d6d",
                color: "white",
                fontWeight: "600",
                padding: "10px 20px",
                borderRadius: "8px",
                transition: "background-color 0.3s ease",
                "&:hover": {
                  backgroundColor: "#ff1f44",
                },
              }}
            >
              {option}
            </Button>
          ))}
        </Box>

        <Typography
          variant="body1"
          sx={{
            fontSize: "18px",
            color: "white",
            fontWeight: "500",
            marginTop: "20px",
            opacity: quizResult ? 1 : 0,
            transform: quizResult ? "translateY(0)" : "translateY(30px)",
            transition: "all 0.8s ease-out",
          }}
        >
          {quizResult}
        </Typography>
      </Box>
    </Box>
  );
};

export default MainPage;

