import React, { useState } from "react";
import { Box, Button, Typography } from "@mui/material";
import videoBg from "../homePage/assets/videoBg.mp4"; // Путь к видео

const MainPage = () => {
  const [isVisible, setIsVisible] = useState(false);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [selectedOption, setSelectedOption] = useState(null);
  const [showResults, setShowResults] = useState(false);

  const questions = [
    {
      question: "Кто стал первым олимпийским чемпионом Кыргызстана?",
      options: [
        "Азимбек Бекболатов",
        "Тамара Третьякова",
        "Айзада Сыдыкова",
        "Улан Джумабеков",
      ],
      answer: "Азимбек Бекболатов",
    },
    {
      question:
        "В каком году Кыргызстан впервые принял участие в Олимпийских играх?",
      options: ["1992", "1996", "2000", "2004"],
      answer: "1992",
    },
    {
      question:
        "Какой спортсмен из Кыргызстана завоевал золотую медаль на Олимпиаде 2016?",
      options: [
        "Улан Джумабеков",
        "Айсулу Тыныбекова",
        "Надежда Скворцова",
        "Бектур Алиев",
      ],
      answer: "Айсулу Тыныбекова",
    },
  ];

  const handleOptionClick = (option) => {
    setSelectedOption(option);
  };

  const handleNextQuestion = () => {
    if (selectedOption === questions[currentQuestionIndex].answer) {
      setShowResults(true);
    } else {
      setCurrentQuestionIndex(currentQuestionIndex + 1);
      setSelectedOption(null);
      setShowResults(false);
    }
  };

  return (
    <Box
      className="home-main-page"
      sx={{
        position: "relative",
        minHeight: "100vh",
        overflow: "hidden",
        display: "flex",
        flexDirection: "column",
      }}
    >
      {/* Видео фон */}
      <Box
        sx={{
          position: "absolute",
          top: 0,
          left: 0,
          width: "100%",
          height: "100%",
          zIndex: -1,
        }}
      >
        <video
          className="background-video"
          src={videoBg}
          autoPlay
          loop
          muted
          playsInline
          style={{
            width: "100%",
            height: "100%",
            objectFit: "cover",
            filter: "brightness(80%)", // Сделал фон темнее для лучшего контраста
          }}
        />
        <div
          style={{
            position: "absolute",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            background:
              "linear-gradient(to bottom, rgba(0,0,0,0.5), rgba(0,0,0,0.9))", // Более темный оверлей
          }}
        ></div>
      </Box>

      {/* Контент с анимацией */}
      <Box
        sx={{
          position: "relative",
          width: "100%",
          minHeight: "100vh",
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          textAlign: "center",
          color: "white",
          padding: "20px",
          opacity: isVisible ? 1 : 0,
          transform: isVisible ? "translateY(0)" : "translateY(30px)", // Меньше движения
          transition: "all 0.8s ease-out",
        }}
      >
        <Box sx={{ maxWidth: "800px" }}>
          <Typography
            variant="h1"
            sx={{
              fontSize: "48px",
              marginBottom: "15px",
              fontWeight: "bold",
              fontFamily: "Georgia, serif",
              letterSpacing: "1px",
            }}
          >
            Спортсмены Кыргызстана
          </Typography>
          <Typography
            variant="body1"
            sx={{
              fontSize: "clamp(16px, 2vw, 22px)",
              fontWeight: "500",
              lineHeight: "1.6",
              maxWidth: "90%",
              margin: "0 auto",
            }}
          >
            Спортсмены Кыргызстана завоевывают признание на международной арене
            в таких видах спорта, как борьба, дзюдо, бокс и легкая атлетика. Эти
            атлеты становятся символами силы, упорства и решимости. Их
            достижения вдохновляют новое поколение, показывая, что стремление к
            победе и трудолюбие открывают путь к вершинам.
          </Typography>
        </Box>
      </Box>

      {/* Блок с изображением с эффектом параллакса */}
      <Box
        sx={{
          textAlign: "center",
          padding: "20px",
          backgroundColor: "#121212",
          display: "flex",
          justifyContent: "center",
          position: "relative",
        }}
      >
        <img
          src="https://data.kaktus.media/image/big/2024-08-11_19-52-39_202865.jpg"
          alt="Кыргызстан"
          style={{
            width: "90%",
            maxWidth: "1400px",
            borderRadius: "12px",
            boxShadow: "0 4px 15px rgba(255, 255, 255, 0.1)",
            transition: "transform 0.3s ease, filter 0.3s ease",
            transform: "scale(1)",
            filter: "brightness(80%)",
          }}
          onMouseOver={(e) => {
            e.target.style.transform = "scale(1.03)";
            e.target.style.filter = "brightness(100%)";
          }}
          onMouseOut={(e) => {
            e.target.style.transform = "scale(1)";
            e.target.style.filter = "brightness(80%)";
          }}
        />
      </Box>

      {/* Викторина */}
      <Box
        sx={{
          textAlign: "center",
          padding: "30px",
          backgroundColor: "#222",
          color: "white",
          maxWidth: "600px",
          margin: "30px auto",
          borderRadius: "10px",
          boxShadow: "0 4px 15px rgba(255, 255, 255, 0.1)",
        }}
      >
        {!showResults ? (
          <>
            <Typography variant="h2" sx={{ marginBottom: "20px" }}>
              Викторина: Олимпийские чемпионы Кыргызстана
            </Typography>
            <Typography variant="body1" sx={{ marginBottom: "15px" }}>
              {questions[currentQuestionIndex].question}
            </Typography>
            <Box
              sx={{
                display: "flex",
                flexDirection: "column",
                gap: "15px",
              }}
            >
              {questions[currentQuestionIndex].options.map((option, index) => (
                <Button
                  key={index}
                  onClick={() => handleOptionClick(option)}
                  variant="contained"
                  sx={{
                    backgroundColor:
                      selectedOption === option ? "#ffd60a" : "#333",
                    color: "white",
                    fontWeight: "bold",
                    transition: "background-color 0.3s, transform 0.2s",
                    "&:hover": {
                      backgroundColor: "#ff4d6d",
                    },
                  }}
                >
                  {option}
                </Button>
              ))}
            </Box>
            <Button
              onClick={handleNextQuestion}
              variant="contained"
              sx={{
                backgroundColor: "#007bff",
                color: "white",
                marginTop: "20px",
                "&:hover": {
                  backgroundColor: "#0056b3",
                },
              }}
            >
              Следующий вопрос
            </Button>
          </>
        ) : (
          <Typography variant="h4" sx={{ marginTop: "20px", color: "#ffd60a" }}>
            Поздравляем! Вы завершили викторину!
          </Typography>
        )}
      </Box>
    </Box>
  );
};

export default MainPage;

