import React from "react";
import { useParams, useNavigate } from "react-router-dom";
import { Typography, Button, Container } from "@mui/material";

const newsData = [
  {
    id: 1,
    title: "Чемпионат Азии по борьбе-2025: у Эрназара Акматалиева - золото!",
    description: "Впервые в своей карьере!!!",
    date: "21:59, 30.03.2025",
    image:
      "https://prosports.kg/upload/news/main//202503/254179_51d2d45eff3b9e8645a242d95de7a921.webp",
    content:
      "Эрназар Акматалиев завоевал первое место на чемпионате Азии по борьбе в 2025 году. Этот успех стал результатом упорных тренировок и настойчивости.\n\nКыргызстанский спортсмен Эрназар Акматалиев стал победителем чемпионата Азии по борьбе в Иордании, сообщает Prosports.kz.\n\nОн выступал 29 марта в весовой категории до 70 кг.\n\nВ 1/8 финала кыргызстанец победил спортсмена из Казахстана (5:4), а затем справился с представителем Узбекистана со счетом 7:1.\n\nАкматалиев вышел в полуфинал, где его соперником стал японский атлет. После первого периода японец вел 5:0, а затем и 6:1, но после этого кыргызстанец смог собраться и вырвал победу.\n\nВ финальной схватке против Виктора Рассадина из Таджикистана Акматалиев снова проигрывал после первого периода, на этот раз со счетом 0:4. Во второй половине схватки кыргызстанец набрал 4 балла и смог вырвать победу.\n\nЭта золотая медаль стала для Акматалиева первой в карьере.",
  },
];

const NewsDetailPage1 = () => {
  const { id } = useParams();
  const newsItem = newsData.find((news) => news.id.toString() === id);
  const navigate = useNavigate();

  if (!newsItem) {
    return <Typography variant="h6">Новость не найдена</Typography>;
  }

  return (
    <Container
      maxWidth="md"
      style={{ padding: "20px", backgroundColor: "#f5f5f5" }}
    >
      <Button
        onClick={() => navigate("/news")}
        style={{
          marginBottom: "20px",
          backgroundColor: "#ff4d6d",
          color: "#fff",
          "&:hover": {
            backgroundColor: "#ff1a4d",
          },
        }}
      >
        Назад к новостям
      </Button>
      <img
        src={newsItem.image}
        alt={newsItem.title}
        style={{ width: "100%", borderRadius: "8px", marginBottom: "20px" }}
      />
      <Typography variant="h4" gutterBottom>
        {newsItem.title}
      </Typography>
      <Typography variant="body2" color="text.secondary" gutterBottom>
        {newsItem.date}
      </Typography>
      {newsItem.content.split("\\n\\n").map((paragraph, index) => (
        <Typography key={index} variant="body1" paragraph>
          {paragraph}
        </Typography>
      ))}
    </Container>
  );
};

export default NewsDetailPage1;
