import "./Navbar.css";
import { Navbar } from "./Navbar.1";

export const styles = {
  root: {
    flexGrow: 1,
    width: "100%",
    height: "auto",
    overflow: "hidden",
  },
  appBar: {
    backgroundColor: "#1a1a1a",
    color: "#ffffff",
    overflow: "hidden",
    width: "auto",
    boxShadow: "0 4px 6px rgba(0, 0, 0, 0.2)", // Shadow for the AppBar
  },
  toolbar: {
    display: "flex",
    justifyContent: "space-between",
    width: "auto",
    overflow: "hidden",
  },
  leftItems: {
    display: "flex",
    alignItems: "center",
    width: "auto",
    height: "40px",
  },
  centerItems: {
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    width: "auto",
    height: "40px",
  },
  rightItems: {
    display: "flex",
    alignItems: "center",
    width: "auto",
    height: "40px",
  },
  menuButton: {
    marginRight: 10,
    overflow: "hidden",
  },
  menuItem: {
    color: "#ffffff",
    transition: "color 0.3s",
    fontSize: "1rem", // Slightly bigger font for better visibility
    fontWeight: "bold", // Uniform text weight for consistency
  },
  icon: {
    width: "auto",
    height: 40,
    marginRight: 10,
  },
  drawerList: {
    width: 250,
    // height: "35%",
    transition: "background-color 0.3s ease",
    // backgroundColor: "rgba(0, 0, 0, 0.1)", // Прозрачный фон
  },
  drawerHeader: {
    display: "flex",
    justifyContent: "flex-end",
    padding: "10px",
    height: "40px",
  },
  closeButton: {
    color: "#000",
    // transition: "color 0.3s",
  },
  drawer: {
    backgroundColor: "rgba(0, 0, 0, 0.9)", // Darker background for a modern look
    transition: "background-color 0.3s ease",
    paddingTop: "10px", // Add padding to top for better alignment
  },
};

export default Navbar;
