import React, { useEffect, useState } from "react";
import {
  Button,
  Box,
  Typography,
  Card,
  CardContent,
  Radio,
  RadioGroup,
  FormControlLabel,
} from "@mui/material";
import videoBg from "../homePage/assets/natureKR.mp4"; // Замените путь на ваш
import ReactConfetti from "react-confetti"; // Библиотека для салюта

const quizQuestions = [
  {
    question: "Столица Кыргызстана?",
    options: ["Бишкек", "Ош", "Каракол", "Талас"],
    answer: "Бишкек",
  },
  {
    question: "Национальная валюта Кыргызстана?",
    options: ["Тенге", "Рубль", "Сом", "Драм"],
    answer: "Сом",
  },
  {
    question: "Самая высокая гора Кыргызстана?",
    options: ["Хан-Тенгри", "Победа", "Тянь-Шань", "Эверест"],
    answer: "Победа",
  },
  {
    question: "Какой вид спорта наиболее популярен в Кыргызстане?",
    options: ["Футбол", "Кок-бору", "Бокс", "Шахматы"],
    answer: "Кок-бору",
  },
  {
    question: "В каком году Кыргызстан получил независимость?",
    options: ["1990", "1991", "1992", "1993"],
    answer: "1991",
  },
  {
    question: "Главное озеро Кыргызстана?",
    options: ["Балхаш", "Иссык-Куль", "Каспий", "Арал"],
    answer: "Иссык-Куль",
  },
  {
    question: "Национальное блюдо Кыргызстана?",
    options: ["Борщ", "Плов", "Бешбармак", "Мампар"],
    answer: "Бешбармак",
  },
  {
    question: "Как называется традиционная юрта?",
    options: ["Гер", "Боз үй", "Чум", "Тепе"],
    answer: "Боз үй",
  },
  {
    question: "Какая крупнейшая река в Кыргызстане?",
    options: ["Чу", "Нарын", "Талас", "Сырдарья"],
    answer: "Нарын",
  },
  {
    question: "Как называется национальный головной убор кыргызов?",
    options: ["Калпак", "Папаха", "Феска", "Кепка"],
    answer: "Калпак",
  },
];

const MainPage = () => {
  const [isVisible, setIsVisible] = useState(false);
  const [selectedAnswers, setSelectedAnswers] = useState({});
  const [showResults, setShowResults] = useState(false);
  const [correctAnswersCount, setCorrectAnswersCount] = useState(0);

  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 200) {
        setIsVisible(true);
      } else {
        setIsVisible(false);
      }
    };
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  const handleSelectAnswer = (question, answer) => {
    setSelectedAnswers({ ...selectedAnswers, [question]: answer });
  };

  const handleSubmitQuiz = () => {
    let correctCount = 0;
    quizQuestions.forEach((item) => {
      if (selectedAnswers[item.question] === item.answer) {
        correctCount++;
      }
    });
    setCorrectAnswersCount(correctCount);
    setShowResults(true);
  };

  return (
    <Box
      className="home-main-page"
      sx={{
        position: "relative",
        minHeight: "100vh",
        overflow: "hidden",
        display: "flex",
        flexDirection: "column",
      }}
    >
      {/* Видео фон */}
      <Box
        sx={{
          position: "absolute",
          top: 0,
          left: 0,
          width: "100%",
          height: "100%",
          zIndex: -1,
        }}
      >
        <video
          className="background-video"
          src={videoBg}
          autoPlay
          loop
          muted
          playsInline
          style={{
            width: "100%",
            height: "100%",
            objectFit: "cover",
            filter: "brightness(80%)", // Сделал фон темнее для лучшего контраста
          }}
        />
        <div
          style={{
            position: "absolute",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            background:
              "linear-gradient(to bottom, rgba(0,0,0,0.5), rgba(0,0,0,0.9))", // Более темный оверлей
          }}
        ></div>
      </Box>

      {/* Контент с анимацией */}
      <Box
        sx={{
          position: "relative",
          width: "100%",
          minHeight: "100vh",
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          textAlign: "center",
          color: "white",
          padding: "20px",
          opacity: isVisible ? 1 : 0,
          transform: isVisible ? "translateY(0)" : "translateY(30px)", // Меньше движения
          transition: "all 0.8s ease-out",
        }}
      >
        <Box sx={{ maxWidth: "800px" }}>
          <Typography
            variant="h1"
            sx={{
              fontSize: "48px",
              marginBottom: "15px",
              fontWeight: "bold",
              fontFamily: "Georgia, serif",
              letterSpacing: "1px",
            }}
          >
            Спортсмены Кыргызстана
          </Typography>
          <Typography
            variant="body1"
            sx={{
              fontSize: "clamp(16px, 2vw, 22px)",
              fontWeight: "500",
              lineHeight: "1.6",
              maxWidth: "90%",
              margin: "0 auto",
            }}
          >
            Спортсмены Кыргызстана завоевывают признание на международной арене
            в таких видах спорта, как борьба, дзюдо, бокс и легкая атлетика. Эти
            атлеты становятся символами силы, упорства и решимости. Их
            достижения вдохновляют новое поколение, показывая, что стремление к
            победе и трудолюбие открывают путь к вершинам.
          </Typography>
        </Box>
      </Box>

      {/* Видео для викторины */}
      <Box
        sx={{
          position: "absolute",
          top: 0,
          left: 0,
          width: "100%",
          height: "100%",
          zIndex: -1,
        }}
      >
        <video
          src={natureKR}
          autoPlay
          loop
          muted
          playsInline
          style={{
            width: "100%",
            height: "100%",
            objectFit: "cover",
            filter: "brightness(50%)",
          }}
        />
      </Box>

      {/* Отображаем вопросы викторины */}
      <Box
        sx={{
          display: "flex",
          flexWrap: "wrap",
          justifyContent: "center",
          gap: "20px",
          padding: "40px",
        }}
      >
        {quizQuestions.map((item, index) => (
          <Card
            key={index}
            sx={{
              width: "300px",
              padding: "20px",
              transition: "0.3s",
              "&:hover": { transform: "scale(1.05)" },
            }}
          >
            <CardContent>
              <Typography variant="h6" sx={{ marginBottom: "10px" }}>
                {item.question}
              </Typography>
              <RadioGroup
                onChange={(e) =>
                  handleSelectAnswer(item.question, e.target.value)
                }
              >
                {item.options.map((option, i) => (
                  <FormControlLabel
                    key={i}
                    value={option}
                    control={<Radio />}
                    label={option}
                  />
                ))}
              </RadioGroup>
              {showResults &&
                selectedAnswers[item.question] === item.answer && (
                  <ReactConfetti
                    width={window.innerWidth}
                    height={window.innerHeight}
                  />
                )}
            </CardContent>
          </Card>
        ))}
      </Box>

      {/* Кнопка для отправки викторины */}
      <Box sx={{ textAlign: "center", marginTop: "20px" }}>
        <Button
          variant="contained"
          onClick={handleSubmitQuiz}
          sx={{
            backgroundColor: "#ff4d6d",
            "&:hover": { backgroundColor: "#d43f5e" },
          }}
        >
          Проверить ответы
        </Button>
      </Box>

      {/* Результаты */}
      {showResults && (
        <Box sx={{ textAlign: "center", marginTop: "40px", color: "#ff4d6d" }}>
          <Typography variant="h4">
            Ваш результат: {correctAnswersCount} из {quizQuestions.length}
          </Typography>
          {correctAnswersCount === quizQuestions.length ? (
            <Typography variant="body1">
              Поздравляем, вы ответили на все вопросы правильно!
            </Typography>
          ) : (
            <Typography variant="body1">
              Попробуйте снова и улучшите свой результат!
            </Typography>
          )}
        </Box>
      )}
    </Box>
  );
};

export default MainPage;

