import React, { useEffect, useState } from "react";
import { Button, Box, Typography } from "@mui/material";
import videoBg from "../homePage/assets/videoBg.mp4"; // Путь к видео

const quizQuestions = [
  {
    question: "Какой год считается годом основания Кыргызстана?",
    options: ["1991", "1924", "1993", "1995"],
    answer: "1991",
  },
  {
    question: "Какой традиционный спорт является популярным в Кыргызстане?",
    options: ["Футбол", "Кок-бору", "Баскетбол", "Бокс"],
    answer: "Кок-бору",
  },
  {
    question: "Какая столица Кыргызстана?",
    options: ["Ош", "Бишкек", "Каракол", "Талас"],
    answer: "Бишкек",
  },
  {
    question: "Какое озеро является крупнейшим в Кыргызстане?",
    options: ["Сары-Челек", "Сон-Куль", "Исык-Куль", "Чатыр-Куль"],
    answer: "Исык-Куль",
  },
  {
    question: "Как называется традиционное кыргызское жилище?",
    options: ["Юрта", "Шатер", "Хижина", "Дом"],
    answer: "Юрта",
  },
  {
    question: "Какой самый высокий пик в Кыргызстане?",
    options: ["Хан-Тенгри", "Ленин", "Победы", "Каракол"],
    answer: "Победы",
  },
  {
    question: "Какой основной язык в Кыргызстане?",
    options: ["Кыргызский", "Русский", "Английский", "Казахский"],
    answer: "Кыргызский",
  },
  {
    question: "Какой национальный напиток популярен в Кыргызстане?",
    options: ["Чай", "Кумыс", "Кефир", "Айран"],
    answer: "Кумыс",
  },
  {
    question: "Какой символ изображен на флаге Кыргызстана?",
    options: ["Орел", "Юрта", "Лошадь", "Горы"],
    answer: "Юрта",
  },
  {
    question: "Сколько областей в Кыргызстане?",
    options: ["5", "7", "9", "6"],
    answer: "7",
  },
];

const MainPage = () => {
  const [isVisible, setIsVisible] = useState(false);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [score, setScore] = useState(0);
  const [showScore, setShowScore] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      setIsVisible(window.scrollY > 200);
    };
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  const handleAnswerClick = (option) => {
    if (option === quizQuestions[currentQuestion].answer) {
      setScore(score + 1);
    }
    const nextQuestion = currentQuestion + 1;
    if (nextQuestion < quizQuestions.length) {
      setCurrentQuestion(nextQuestion);
    } else {
      setShowScore(true);
    }
  };

  return (
    <Box sx={{ position: "relative", minHeight: "100vh", overflow: "hidden" }}>
      {/* Видео фон */}
      <Box
        sx={{
          position: "absolute",
          top: 0,
          left: 0,
          width: "100%",
          height: "100%",
          zIndex: -1,
        }}
      >
        <video
          src={videoBg}
          autoPlay
          loop
          muted
          playsInline
          style={{
            width: "100%",
            height: "100%",
            objectFit: "cover",
            filter: "brightness(80%)",
          }}
        />
      </Box>

      {/* Контент */}
      <Box sx={{ textAlign: "center", padding: "20px", color: "white" }}>
        <Typography variant="h1" sx={{ fontSize: "48px", fontWeight: "bold" }}>
          Спортсмены Кыргызстана
        </Typography>
        <Typography
          variant="body1"
          sx={{ fontSize: "20px", marginTop: "10px" }}
        >
          Спортсмены Кыргызстана добиваются успехов на международной арене!
        </Typography>
      </Box>

      {/* Изображение */}
      <Box sx={{ textAlign: "center", padding: "20px" }}>
        <img
          src="https://data.kaktus.media/image/big/2024-08-11_19-52-39_202865.jpg"
          alt="Кыргызстан"
          style={{ width: "90%", maxWidth: "1400px", borderRadius: "12px" }}
        />
      </Box>

      {/* Викторина */}
      <Box
        sx={{
          textAlign: "center",
          padding: "40px",
          backgroundColor: "rgba(0,0,0,0.8)",
          color: "white",
        }}
      >
        <Typography
          variant="h2"
          sx={{ fontSize: "36px", marginBottom: "20px" }}
        >
          Викторина о Кыргызстане
        </Typography>
        {showScore ? (
          <Typography variant="h4">
            Вы набрали {score} из {quizQuestions.length}!
          </Typography>
        ) : (
          <Box>
            <Typography variant="h5">
              {quizQuestions[currentQuestion].question}
            </Typography>
            {quizQuestions[currentQuestion].options.map((option) => (
              <Button
                key={option}
                variant="contained"
                sx={{
                  display: "block",
                  margin: "10px auto",
                  backgroundColor: "orange",
                }}
                onClick={() => handleAnswerClick(option)}
              >
                {option}
              </Button>
            ))}
          </Box>
        )}
      </Box>
    </Box>
  );
};

export default MainPage;

// Courier New, monospace

