import React from "react";
import AddAthletes from "../components/products/AddAthletes";
import AddCategory from "../components/products/AddCategory";
import { useAuth } from "../context/AuthContextProvider"; // Импортируйте ваш контекст авторизации

const AdminPage = () => {
  const { user } = useAuth();

  // Проверка, является ли пользователь администратором
  const isAdmin = user && user.email === "admin@example.com"; // Пример для проверки по email

  if (!isAdmin) {
    return <div>У вас нет прав для доступа к этой странице.</div>;
  }

  return (
    <div
      style={{
        position: "relative", // Позиционирование для родительского контейнера
        backgroundImage: "url(/images/AdminPage.jpeg)",
        backgroundSize: "cover",
        backgroundPosition: "center",
        height: "100vh",
        width: "100vw",
        marginTop: "-100px",
      }}
    >
      {/* Черная полупрозрачная обводка сверху */}
      <div
        style={{
          position: "absolute",
          top: 0,
          left: 0,
          width: "100%",
          height: "100%", // Высота обводки теперь 100%
          backgroundColor: "rgba(0, 0, 0, 0.2)", // Черный цвет с полупрозрачностью
          zIndex: 1, // Обводка должна быть ниже карточек
        }}
      ></div>

      {/* Карточки AddAthletes и AddCategory */}
      <div style={{ position: "relative", zIndex: 2 }}>
        <AddAthletes />
        <AddCategory />
      </div>
    </div>
  );
};

export default AdminPage;
