Modal.setAppElement("#root"); // Устанавливаем корневой элемент для экранного чтеца

const SportsPage = () => {
  const [modalIsOpen, setModalIsOpen] = useState(false); // Состояние для открытия/закрытия модального окна
  const [selectedSport, setSelectedSport] = useState(null); // Состояние для хранения выбранного спорта

  // Функция для открытия модального окна с данными выбранного спорта
  const openModal = (sport) => {
    setSelectedSport(sport); // Устанавливаем выбранный спорт
    setModalIsOpen(true); // Открываем модальное окно
  };

  // Функция для закрытия модального окна
  const closeModal = () => {
    setModalIsOpen(false); // Закрываем модальное окно
    setSelectedSport(null); // Сбрасываем выбранный спорт
  };
  return (
    <div className="sports-page">
      <h1>Популярные виды спорта</h1>
      <div className="sports-container">
        {sports.map((sport, index) => (
          <div
            key={index}
            className="sport-card"
            onClick={() => openModal(sport)} // Вызываем openModal при клике на карточку спорта
          >
            <img src={sport.imageUrl} alt={sport.name} />
            <h2>{sport.name}</h2>
          </div>
        ))}
      </div>
      <Modal
        isOpen={modalIsOpen}
        onRequestClose={closeModal}
        contentLabel="Sport Details"
        className="modal"
        overlayClassName="overlay"
      >
        {selectedSport && ( // Проверяем, что selectedSport установлен перед отображением данных
          <div>
            <h2>{selectedSport.name}</h2>
            <img src={selectedSport.imageUrl} alt={selectedSport.name} />
            <p>{selectedSport.description}</p>
            <a
              href={selectedSport.link}
              target="_blank"
              rel="noopener noreferrer"
            ></a>
            <button
              variant="contained"
              size="small"
              color="secondary"
              onClick={closeModal}
            >
              Закрыть
            </button>
          </div>
        )}
      </Modal>
    </div>
  );
};

export default SportsPage;
