import React, { useState } from "react";
import {
  Button,
  Box,
  Typography,
  Card,
  CardContent,
  TextField,
} from "@mui/material";

const MainPage = () => {
  const [isVisible, setIsVisible] = useState(false);
  const [questionIndex, setQuestionIndex] = useState(0);
  const [answers, setAnswers] = useState([]);
  const [showResult, setShowResult] = useState(false);

  const questions = [
    {
      question: "Какая страна на первом месте по медалям в Олимпийских играх?",
      options: ["США", "Китай", "Россия", "Германия"],
      answer: "США",
    },
    {
      question: "В каком году были проведены первые Олимпийские игры?",
      options: ["1896", "1900", "1924", "1936"],
      answer: "1896",
    },
    {
      question:
        "Какой спортсмен стал олимпийским чемпионом в беге на 100 м на последних Олимпийских играх?",
      options: [
        "Юсейн Болт",
        "Кристиан Коллман",
        "Ламонт Марселл Якобс",
        "Майкл Джонсон",
      ],
      answer: "Ламонт Марселл Якобс",
    },
  ];

  const handleAnswer = (selectedAnswer) => {
    setAnswers([...answers, selectedAnswer]);
    if (questionIndex < questions.length - 1) {
      setQuestionIndex(questionIndex + 1);
    } else {
      setShowResult(true);
    }
  };

  return (
    <Box
      className="home-main-page"
      sx={{
        position: "relative",
        minHeight: "100vh",
        overflow: "hidden",
        display: "flex",
        flexDirection: "column",
      }}
    >
      {/* Видео фон */}
      <Box
        sx={{
          position: "absolute",
          top: 0,
          left: 0,
          width: "100%",
          height: "100%",
          zIndex: -1,
        }}
      >
        <video
          className="background-video"
          src={videoBg}
          autoPlay
          loop
          muted
          playsInline
          style={{
            width: "100%",
            height: "100%",
            objectFit: "cover",
            filter: "brightness(70%)", // Сделал фон темнее для лучшего контраста
          }}
        />
        <div
          style={{
            position: "absolute",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            background:
              "linear-gradient(to bottom, rgba(0,0,0,0.5), rgba(0,0,0,0.9))", // Более темный оверлей
          }}
        ></div>
      </Box>

      {/* Контент с анимацией */}
      <Box
        sx={{
          position: "relative",
          width: "100%",
          minHeight: "100vh",
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          textAlign: "center",
          color: "white",
          padding: "20px",
          opacity: isVisible ? 1 : 0,
          transform: isVisible ? "translateY(0)" : "translateY(30px)", // Меньше движения
          transition: "all 0.8s ease-out",
        }}
      >
        <Box sx={{ maxWidth: "800px" }}>
          <Typography
            variant="h1"
            sx={{
              fontSize: "48px",
              marginBottom: "15px",
              fontWeight: "bold",
              fontFamily: "Georgia, serif",
              letterSpacing: "1px",
            }}
          >
            Спортсмены Кыргызстана
          </Typography>
          <Typography
            variant="body1"
            sx={{
              fontSize: "clamp(16px, 2vw, 22px)",
              fontWeight: "500",
              lineHeight: "1.6",
              maxWidth: "90%",
              margin: "0 auto",
            }}
          >
            Спортсмены Кыргызстана завоевывают признание на международной арене
            в таких видах спорта, как борьба, дзюдо, бокс и легкая атлетика. Эти
            атлеты становятся символами силы, упорства и решимости. Их
            достижения вдохновляют новое поколение, показывая, что стремление к
            победе и трудолюбие открывают путь к вершинам.
          </Typography>
        </Box>
      </Box>

      {/* Блок с изображением с эффектом параллакса */}
      <Box
        sx={{
          textAlign: "center",
          padding: "20px",
          backgroundColor: "#121212",
          display: "flex",
          justifyContent: "center",
          position: "relative",
        }}
      >
        <img
          src="https://data.kaktus.media/image/big/2024-08-11_19-52-39_202865.jpg"
          alt="Кыргызстан"
          style={{
            width: "90%",
            maxWidth: "1400px",
            borderRadius: "12px",
            boxShadow: "0 4px 15px rgba(255, 255, 255, 0.1)",
            transition: "transform 0.3s ease, filter 0.3s ease",
            transform: "scale(1)",
            filter: "brightness(80%)",
          }}
          onMouseOver={(e) => {
            e.target.style.transform = "scale(1.03)";
            e.target.style.filter = "brightness(100%)";
          }}
          onMouseOut={(e) => {
            e.target.style.transform = "scale(1)";
            e.target.style.filter = "brightness(80%)";
          }}
        />
      </Box>

      {/* Викторина */}
      <Box
        sx={{
          textAlign: "center",
          padding: "50px 20px",
          backgroundColor: "#2C2C2C",
          borderRadius: "10px",
          boxShadow: "0 4px 20px rgba(0, 0, 0, 0.2)",
          marginTop: "50px",
          transition: "transform 0.3s ease-in-out",
          ":hover": { transform: "scale(1.02)" },
        }}
      >
        <Typography
          variant="h4"
          sx={{
            fontWeight: "bold",
            color: "#fff",
            marginBottom: "20px",
            fontFamily: "Arial, sans-serif",
          }}
        >
          Викторина о Спорте
        </Typography>
        {!showResult ? (
          <>
            <Typography
              variant="h6"
              sx={{
                color: "#fff",
                marginBottom: "20px",
                fontFamily: "Georgia, serif",
              }}
            >
              {questions[questionIndex].question}
            </Typography>
            <Box
              sx={{
                display: "flex",
                justifyContent: "center",
                flexDirection: "column",
              }}
            >
              {questions[questionIndex].options.map((option, index) => (
                <Button
                  key={index}
                  variant="contained"
                  color="primary"
                  sx={{
                    margin: "10px 0",
                    padding: "10px 20px",
                    fontSize: "16px",
                    backgroundColor: "#ff4d6d",
                    ":hover": { backgroundColor: "#e23b5a" },
                  }}
                  onClick={() => handleAnswer(option)}
                >
                  {option}
                </Button>
              ))}
            </Box>
          </>
        ) : (
          <Typography
            variant="h5"
            sx={{
              fontWeight: "bold",
              color: "#fff",
              marginTop: "20px",
            }}
          >
            Результаты:{" "}
            {
              answers.filter(
                (answer, index) => answer === questions[index].answer
              ).length
            }{" "}
            из {questions.length} правильных ответов.
          </Typography>
        )}
      </Box>
    </Box>
  );
};

export default MainPage;

