import React, { useEffect, useState } from "react";
import { Button, Box, Typography } from "@mui/material";
import videoBg from "../homePage/assets/videoBg.mp4"; // Путь к видео

const MainPage = () => {
  const [isVisible, setIsVisible] = useState(false);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [score, setScore] = useState(0);
  const [showScore, setShowScore] = useState(false);

  const questions = [
    {
      question: "Какой город является столицей Кыргызстана?",
      answers: ["Ош", "Бишкек", "Нарын", "Токмок"],
      correct: 1,
    },
    {
      question: "Какое озеро является крупнейшим в Кыргызстане?",
      answers: ["Сон-Куль", "Чатыр-Куль", "Иссык-Куль", "Кара-Суу"],
      correct: 2,
    },
    {
      question: "Какой национальный спорт популярен в Кыргызстане?",
      answers: ["Кок-бору", "Футбол", "Волейбол", "Баскетбол"],
      correct: 0,
    },
    {
      question: "Какая валюта используется в Кыргызстане?",
      answers: ["Тенге", "Рубль", "Сом", "Манат"],
      correct: 2,
    },
    {
      question: "Какой известный пик находится в Кыргызстане?",
      answers: ["Пик Ленина", "Эверест", "Арарат", "Пик Чапаева"],
      correct: 0,
    },
    {
      question: "Какой год считается годом независимости Кыргызстана?",
      answers: ["1991", "1985", "2000", "1995"],
      correct: 0,
    },
    {
      question: "Как называется традиционный дом кочевников в Кыргызстане?",
      answers: ["Юрта", "Хижина", "Домик", "Палатка"],
      correct: 0,
    },
    {
      question: "Какой язык является государственным в Кыргызстане?",
      answers: ["Русский", "Киргизский", "Казахский", "Турецкий"],
      correct: 1,
    },
    {
      question: "Как называется традиционное блюдо из мяса и теста?",
      answers: ["Плов", "Манты", "Лагман", "Самса"],
      correct: 1,
    },
    {
      question: "Какая гора является самой высокой в Кыргызстане?",
      answers: ["Хан-Тенгри", "Победы", "Манас", "Айгуль-Тоо"],
      correct: 1,
    },
  ];

  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 200) {
        setIsVisible(true);
      } else {
        setIsVisible(false);
      }
    };
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  const handleAnswerClick = (index) => {
    if (index === questions[currentQuestion].correct) {
      setScore(score + 1);
    }
    const nextQuestion = currentQuestion + 1;
    if (nextQuestion < questions.length) {
      setCurrentQuestion(nextQuestion);
    } else {
      setShowScore(true);
    }
  };

  return (
    <Box
      className="home-main-page"
      sx={{
        position: "relative",
        minHeight: "100vh",
        overflow: "hidden",
        display: "flex",
        flexDirection: "column",
      }}
    >
      <Box
        sx={{
          position: "absolute",
          top: 0,
          left: 0,
          width: "100%",
          height: "100%",
          zIndex: -1,
        }}
      >
        <video
          className="background-video"
          src={videoBg}
          autoPlay
          loop
          muted
          playsInline
          style={{
            width: "100%",
            height: "100%",
            objectFit: "cover",
            filter: "brightness(80%)",
          }}
        />
        <div
          style={{
            position: "absolute",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            background:
              "linear-gradient(to bottom, rgba(0,0,0,0.5), rgba(0,0,0,0.9))",
          }}
        ></div>
      </Box>

      <Box
        sx={{
          position: "relative",
          width: "100%",
          minHeight: "100vh",
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          textAlign: "center",
          color: "white",
          padding: "20px",
          opacity: isVisible ? 1 : 0,
          transform: isVisible ? "translateY(0)" : "translateY(30px)",
          transition: "all 0.8s ease-out",
        }}
      >
        <Box sx={{ maxWidth: "800px" }}>
          <Typography
            variant="h1"
            sx={{
              fontSize: "48px",
              marginBottom: "15px",
              fontWeight: "bold",
              fontFamily: "Georgia, serif",
              letterSpacing: "1px",
            }}
          >
            Спортсмены Кыргызстана
          </Typography>
          <Typography
            variant="body1"
            sx={{
              fontSize: "clamp(16px, 2vw, 22px)",
              fontWeight: "500",
              lineHeight: "1.6",
              maxWidth: "90%",
              margin: "0 auto",
            }}
          >
            Спортсмены Кыргызстана завоевывают признание на международной арене
            в таких видах спорта, как борьба, дзюдо, бокс и легкая атлетика. Эти
            атлеты становятся символами силы, упорства и решимости. Их
            достижения вдохновляют новое поколение, показывая, что стремление к
            победе и трудолюбие открывают путь к вершинам.
          </Typography>
        </Box>
      </Box>

      <Box
        sx={{
          textAlign: "center",
          padding: "20px",
          backgroundColor: "#121212",
          display: "flex",
          justifyContent: "center",
          position: "relative",
        }}
      >
        <img
          src="https://data.kaktus.media/image/big/2024-08-11_19-52-39_202865.jpg"
          alt="Кыргызстан"
          style={{
            width: "90%",
            maxWidth: "1400px",
            borderRadius: "12px",
            boxShadow: "0 4px 15px rgba(255, 255, 255, 0.1)",
            transition: "transform 0.3s ease, filter 0.3s ease",
            transform: "scale(1)",
            filter: "brightness(80%)",
          }}
          onMouseOver={(e) => {
            e.target.style.transform = "scale(1.03)";
            e.target.style.filter = "brightness(100%)";
          }}
          onMouseOut={(e) => {
            e.target.style.transform = "scale(1)";
            e.target.style.filter = "brightness(80%)";
          }}
        />
      </Box>

      <Box sx={{ textAlign: "center", padding: "20px" }}>
        {showScore ? (
          <Typography variant="h4">
            Вы набрали {score} из {questions.length}
          </Typography>
        ) : (
          <Typography variant="h5">
            {questions[currentQuestion].question}
          </Typography>
        )}
        {!showScore &&
          questions[currentQuestion].answers.map((answer, index) => (
            <Button
              key={index}
              variant="contained"
              onClick={() => handleAnswerClick(index)}
            >
              {answer}
            </Button>
          ))}
      </Box>
    </Box>
  );
};

export default MainPage;

// Courier New, monospace

